import { useState } from 'react';
import {
  Grid, TextField, Chip, Card, CardContent, Typography,
  Button, IconButton, Modal, Box
} from '@mui/material';
import {
  Search, EventSeat, Theaters, Close
} from '@mui/icons-material';

// Mock data
const movies = [
  {
    id: 1,
    title: "Avengers: Endgame",
    genre: "Action",
    timing: "2025-03-15T18:30",
    seats: [
      { id: 'A1', type: 'VIP', price: 1200, available: true },
      { id: 'A2', type: 'VIP', price: 1200, available: false },
      { id: 'B1', type: 'Regular', price: 800, available: true },
    ],
    theater: "PVR Cinemas: Forum Mall"
  }
];

const MovieCard = ({ movie, onSelect }) => (
  <Card sx={{ mb: 2, cursor: 'pointer' }} onClick={() => onSelect(movie)}>
    <CardContent>
      <Typography variant="h6">{movie.title}</Typography>
      <Typography variant="body2" color="text.secondary">
        {movie.theater}
      </Typography>
      <Typography variant="caption" display="block">
        {new Date(movie.timing).toLocaleString()} | {movie.genre}
      </Typography>
    </CardContent>
  </Card>
);

const SeatMapModal = ({ movie, open, onClose, onBook }) => {
  const [selectedSeats, setSelectedSeats] = useState([]);

  const handleSeatClick = (seat) => {
    if (!seat.available) return;
    setSelectedSeats(prev => 
      prev.includes(seat.id) 
        ? prev.filter(id => id !== seat.id)
        : [...prev, seat.id]
    );
  };

  const total = selectedSeats.reduce((sum, seatId) => {
    const seat = movie.seats.find(s => s.id === seatId);
    return sum + (seat?.price || 0);
  }, 0);

  return (
    <Modal open={open} onClose={onClose}>
      <Box sx={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: '80%',
        bgcolor: 'background.paper',
        boxShadow: 24,
        p: 4,
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <Typography variant="h5">{movie.title} - Seat Selection</Typography>
          <IconButton onClick={onClose}><Close /></IconButton>
        </div>

        <Grid container spacing={2} sx={{ mt: 2 }}>
          {/* Seat Map */}
          <Grid item xs={8}>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(10, 1fr)', gap: '8px' }}>
              {movie.seats.map(seat => (
                <IconButton
                  key={seat.id}
                  color={
                    selectedSeats.includes(seat.id) ? 'primary' : 
                    seat.available ? 'success' : 'error'
                  }
                  disabled={!seat.available}
                  onClick={() => handleSeatClick(seat)}
                >
                  <EventSeat fontSize="large" />
                  <Typography variant="caption" sx={{ position: 'absolute' }}>
                    {seat.id}
                  </Typography>
                </IconButton>
              ))}
            </div>
          </Grid>

          {/* Booking Summary */}
          <Grid item xs={4}>
            <Card elevation={3} sx={{ p: 2 }}>
              <Typography variant="h6">Booking Summary</Typography>
              <Typography variant="body2" sx={{ mt: 2 }}>
                Movie: {movie.title}
              </Typography>
              <Typography variant="body2">
                Theater: {movie.theater}
              </Typography>
              <Typography variant="body2" sx={{ mt: 2 }}>
                Selected Seats: {selectedSeats.join(', ')}
              </Typography>
              <Typography variant="h6" sx={{ mt: 2 }}>
                Total: â‚¹{total}
              </Typography>
              <Button
                variant="contained"
                fullWidth
                sx={{ mt: 2 }}
                disabled={selectedSeats.length === 0}
                onClick={() => onBook(selectedSeats)}
              >
                Confirm Booking
              </Button>
            </Card>
          </Grid>
        </Grid>
      </Box>
    </Modal>
  );
};

const Dashboard = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [filterGenre, setFilterGenre] = useState('all');

  const handleBookSeats = (seats) => {
    alert(`Booking confirmed for seats: ${seats.join(', ')}`);
    setSelectedMovie(null);
  };

  return (
    <div style={{ padding: '24px', maxWidth: '1200px', margin: '0 auto' }}>
      {/* Search and Filters */}
      <TextField
        fullWidth
        placeholder="Search movies..."
        InputProps={{ startAdornment: <Search /> }}
        sx={{ mb: 2 }}
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      <div style={{ marginBottom: '24px' }}>
        <Chip label="All" onClick={() => setFilterGenre('all')} />
        <Chip label="Action" onClick={() => setFilterGenre('Action')} sx={{ ml: 1 }} />
        <Chip label="Comedy" onClick={() => setFilterGenre('Comedy')} sx={{ ml: 1 }} />
      </div>

      {/* Movie List */}
      <Grid container spacing={3}>
        {movies
          .filter(movie => 
            movie.title.toLowerCase().includes(searchQuery.toLowerCase()) &&
            (filterGenre === 'all' || movie.genre === filterGenre)
          )
          .map(movie => (
            <Grid item xs={12} md={6} key={movie.id}>
              <MovieCard 
                movie={movie} 
                onSelect={() => setSelectedMovie(movie)}
              />
            </Grid>
          ))}
      </Grid>

      {/* Seat Selection Modal */}
      {selectedMovie && (
        <SeatMapModal
          movie={selectedMovie}
          open={!!selectedMovie}
          onClose={() => setSelectedMovie(null)}
          onBook={handleBookSeats}
        />
      )}
    </div>
  );
};

export default Dashboard;
