import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Header from '../components/Header';
import BookingModal from '../components/BookingModal';
import './EventDetailsPage.css';


const EventDetailsPage = () => {
  const { eventId } = useParams();
  const [event, setEvent] = useState(null);
  const [showBookingModal, setShowBookingModal] = useState(false);
  const [loading, setLoading] = useState(true);

  // Mock data for event details
  useEffect(() => {
    // Simulate API call to get event details
    setTimeout(() => {
      const mockEvents = [
        {
          id: "1",
          name: "FINK India 2025 - Mumbai",
          date: "2025-04-03",
          time: "8:00 PM",
          location: "Antisocial - Lower Parel: Mumbai",
          image: "https://images.unsplash.com/photo-1529156069898-49953e39b3ac",
          description: "Folk, Indie | English | 21yrs+ | 4hrs",
          category: "Music Shows",
          language: "English",
          price: 999,
          duration: "4hrs",
          ageRestriction: "21yrs+",
          bannerImage: "https://images.unsplash.com/photo-1529156069898-49953e39b3ac"
        },
        {
          id: "2",
          name: "Pune Comedy Festival 2025",
          date: "2025-04-04",
          time: "7:00 PM",
          location: "Pune Convention Center",
          image: "https://images.unsplash.com/photo-1515169067865-dae1abe0b6b3",
          description: "Stand-up Comedy | Hindi | All Ages | 2hrs",
          category: "Comedy Shows",
          language: "Hindi",
          price: 499,
          duration: "2hrs",
          ageRestriction: "All Ages",
          bannerImage: "https://images.unsplash.com/photo-1515169067865-dae1abe0b6b3"
        },
        {
          id: "3",
          name: "Guns N' Roses: India Tour 2025",
          date: "2025-05-17",
          time: "6:00 PM",
          location: "Wankhede Stadium, Mumbai",
          image: "https://images.unsplash.com/photo-1519671482749-fd09be7ccebf",
          description: "Rock Concert | English | All Ages | 3hrs",
          category: "Music Shows",
          language: "English",
          price: 1999,
          duration: "3hrs",
          ageRestriction: "All Ages",
          bannerImage: "https://images.unsplash.com/photo-1519671482749-fd09be7ccebf"
        },
        {
          id: "4",
          name: "All Hearts Tour - Shreya Ghoshal Live",
          date: "2025-05-10",
          time: "7:30 PM",
          location: "Jio World Garden, Mumbai",
          image: "https://images.unsplash.com/photo-1559628232-ded576a4adbc",
          description: "Bollywood Music | Hindi | All Ages | 2hrs",
          category: "Music Shows",
          language: "Hindi",
          price: 1499,
          duration: "2hrs",
          ageRestriction: "All Ages",
          bannerImage: "https://images.unsplash.com/photo-1559628232-ded576a4adbc"
        }
      ];

      const foundEvent = mockEvents.find(e => e.id === eventId);
      setEvent(foundEvent);
      setLoading(false);
    }, 1000);
  }, [eventId]);

  const handleBookNow = () => {
    setShowBookingModal(true);
  };

  const handleCloseModal = () => {
    setShowBookingModal(false);
  };

  const handleConfirmBooking = (bookingDetails) => {
    // This would typically involve an API call to your backend
    console.log('Booking confirmed:', bookingDetails);
    alert(`Booking confirmed for ${event.name}!`);
    setShowBookingModal(false);
  };

  if (loading) {
    return <div className="loading">Loading event details...</div>;
  }

  if (!event) {
    return <div className="not-found">Event not found</div>;
  }

  const eventDate = new Date(event.date);
  const formattedDate = eventDate.toLocaleDateString('en-US', {
    weekday: 'short',
    day: 'numeric',
    month: 'short',
    year: 'numeric'
  });

  return (
    <div className="event-details-page">
      <Header />

      {/* Event Banner */}
      <div className="event-banner">
        <img src={event.bannerImage} alt={event.name} />
      </div>

      {/* Event Details */}
      <div className="event-details-container">
        <div className="event-details-content">
          <h1 className="event-title">{event.name}</h1>
          <div className="event-metadata">
            <span className="event-genre">{event.category}</span>
            <span className="event-language">{event.language}</span>
            <span className="event-age">{event.ageRestriction}</span>
            <span className="event-duration">{event.duration}</span>
          </div>

          <div className="event-schedule">
            <div className="event-date-time">
              <h3>{formattedDate} at {event.time}</h3>
              <p>{event.location}</p>
            </div>
            <div className="event-price">
              <span className="price-tag">₹ {event.price}</span>
              <div className="filling-fast">FILLING FAST</div>
            </div>
          </div>

          <button className="book-now-btn" onClick={handleBookNow}>
            Book
          </button>

          <div className="event-description">
            <h3>About</h3>
            <p>{event.description}</p>
          </div>

          <div className="share-section">
            <h4>Share this event</h4>
            <div className="share-buttons">
              <button className="share-btn facebook">
                <i className="fab fa-facebook-f"></i>
              </button>
              <button className="share-btn twitter">
                <i className="fab fa-twitter"></i>
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Booking Modal */}
      {showBookingModal && (
        <BookingModal 
          event={event} 
          onClose={handleCloseModal} 
          onConfirm={handleConfirmBooking} 
        />
      )}
    </div>
  );
};

export default EventDetailsPage;
