import React, { useState } from 'react';
import './SeatSelectionPage.css';
import { useLocation, useNavigate } from 'react-router-dom';

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state?.event;

  if (!event) {
    return <div className="error">Event data missing. Please go back.</div>;
  }

  const rows = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H'];
  const seatsPerRow = 14;
  const lowPrice = 150;
  const highPrice = 250;

  const [selectedSeats, setSelectedSeats] = useState([]);

  const toggleSeat = (row, number) => {
    const seatId = `${row}${number}`;
    setSelectedSeats((prev) =>
      prev.includes(seatId)
        ? prev.filter((s) => s !== seatId)
        : [...prev, seatId]
    );
  };

  const handleContinue = () => {
    if (selectedSeats.length === 0) {
      alert('Please select at least one seat.');
      return;
    }

    const total = selectedSeats.reduce((sum, seat) => {
      const row = seat[0];
      return sum + (['A', 'B'].includes(row) ? lowPrice : highPrice);
    }, 0);

    navigate('/payment', {
      state: {
        title: event.title,
        price: total,
        quantity: selectedSeats.length,
        selectedSeats
      }
    });
  };

  return (
    <div className="seat-selection-page">
      <h2>Select Seats for {event.title}</h2>

      <div className="seat-container">
        {rows.map((row, rowIndex) => (
          <div
            key={row}
            className={`seat-row ${
              row === 'B' ? 'gap-after-row' : ''
            }`}
          >
            {[...Array(seatsPerRow)].map((_, i) => {
              const seatId = `${row}${i + 1}`;
              const isSelected = selectedSeats.includes(seatId);
              const isLowTier = ['A', 'B'].includes(row);

              return (
                <div
                  key={seatId}
                  className={`seat ${isSelected ? 'selected' : ''} ${
                    isLowTier ? 'low-tier' : 'high-tier'
                  }`}
                  onClick={() => toggleSeat(row, i + 1)}
                >
                  {seatId}
                </div>
              );
            })}
          </div>
        ))}

        <div className="screen">SCREEN</div>
      </div>

      <div className="seat-summary">
        <p>
          Selected Seats:{' '}
          {selectedSeats.length > 0 ? selectedSeats.join(', ') : 'None'}
        </p>
        <p>
          Total: â‚¹
          {selectedSeats.reduce((sum, seat) => {
            const row = seat[0];
            return sum + (['A', 'B'].includes(row) ? lowPrice : highPrice);
          }, 0)}
        </p>
        <button onClick={handleContinue}>
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
