// src/pages/EventDetailsPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import BookingModal from '../components/BookingModal';
import './EventDetailsPage.css';

const EventDetailsPage = () => {
  const { eventId } = useParams();
  const [event, setEvent] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    // Mock API call
    const fetchEvent = async () => {
      const mockEvent = {
        id: 1,
        title: "Coldplay: Music of the Spheres World Tour",
        type: "concert",
        date: "2025-06-15",
        time: "7:30 PM",
        price: 5000,
        venue: "DY Patil Stadium, Mumbai",
        poster: "https://source.unsplash.com/random/1600x900?concert",
        details: {
          artists: ["Coldplay"],
          sponsors: ["Coca-Cola", "Visa"],
          duration: "2h 30m",
          organizer: "Live Nation"
        }
      };
      setEvent(mockEvent);
    };
    fetchEvent();
  }, [eventId]);

  if (!event) return <div className="loading">Loading...</div>;

  return (
    <div className="event-details-page">
      <div className="event-hero">
        <img src={event.poster} alt={event.title} className="event-poster" />
      </div>

      <div className="event-content container">
        <h1 className="event-title">{event.title}</h1>

        <div className="event-meta">
          <div className="meta-item">
            <span className="meta-label">Date</span>
            <span className="meta-value">
              {new Date(event.date).toLocaleDateString('en-IN')}
            </span>
          </div>
          <div className="meta-item">
            <span className="meta-label">Time</span>
            <span className="meta-value">{event.time}</span>
          </div>
          <div className="meta-item">
            <span className="meta-label">Venue</span>
            <span className="meta-value">{event.venue}</span>
          </div>
        </div>

        {event.type === 'concert' && (
          <div className="details-section">
            <h2 className="section-title">Event Details</h2>
            <div className="detail-item">
              <span>Artists:</span>
              <div className="artist-list">
                {event.details.artists.map((artist, i) => (
                  <span key={i} className="artist-tag">{artist}</span>
                ))}
              </div>
            </div>
            <div className="detail-item">
              <span>Sponsors:</span>
              <div className="sponsor-list">
                {event.details.sponsors.map((sponsor, i) => (
                  <span key={i} className="sponsor-tag">{sponsor}</span>
                ))}
              </div>
            </div>
          </div>
        )}

        {event.type === 'movie' && (
          <div className="details-section">
            <h2 className="section-title">Movie Details</h2>
            <div className="detail-item">
              <span>Director:</span>
              <span>Anthony and Joe Russo</span>
            </div>
            <div className="detail-item">
              <span>Cast:</span>
              <div className="cast-list">
                {["Robert Downey Jr.", "Chris Evans", "Mark Ruffalo"].map((actor, i) => (
                  <span key={i} className="cast-tag">{actor}</span>
                ))}
              </div>
            </div>
          </div>
        )}

        <div className="booking-cta">
          <div className="price-container">
            <span className="price-label">Starting from</span>
            <span className="price">₹{event.price}</span>
          </div>
          <button 
            className="book-button"
            onClick={() => setShowModal(true)}
          >
            Book Tickets
          </button>
        </div>
      </div>

      {showModal && <BookingModal event={event} onClose={() => setShowModal(false)} />}
    </div>
  );
};

export default EventDetailsPage;
