// src/pages/EventDetailsPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import BookingModal from '../components/BookingModal';
import './EventDetailsPage.css';

const EventDetailsPage = () => {
  const { eventId } = useParams();
  const [event, setEvent] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    // Mock API call
    const fetchEvent = async () => {
      const mockEvent = {
        id: 1,
        title: "Coldplay Concert",
        type: "concert",
        date: "2025-06-15",
        time: "8:00 PM",
        price: 5000,
        venue: "Mumbai Arena",
        poster: "https://via.placeholder.com/800x400",
        details: {
          artists: ["Coldplay"],
          sponsors: ["Coca-Cola", "Visa"],
          duration: "2h 30m"
        }
      };
      setEvent(mockEvent);
    };
    fetchEvent();
  }, [eventId]);

  if (!event) return <div className="loading">Loading...</div>;

  return (
    <div className="event-details-page">
      <img src={event.poster} alt={event.title} className="event-poster" />

      <div className="event-content">
        <h1 className="event-title">{event.title}</h1>

        <div className="event-meta">
          <span>{new Date(event.date).toLocaleDateString()}</span>
          <span>{event.time}</span>
          <span>{event.venue}</span>
        </div>

        {event.type === 'concert' && (
          <div className="details-section">
            <h3>Artists</h3>
            <ul>
              {event.details.artists.map((artist, i) => (
                <li key={i}>{artist}</li>
              ))}
            </ul>
          </div>
        )}

        <div className="booking-section">
          <div className="price">â‚¹{event.price}</div>
          <button className="book-button" onClick={() => setShowModal(true)}>
            Book Now
          </button>
        </div>
      </div>

      {showModal && <BookingModal event={event} onClose={() => setShowModal(false)} />}
    </div>
  );
};

export default EventDetailsPage;
