import React, { useEffect, useState } from 'react';
import './SeatSelectionPage.css';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const SeatSelectionPage = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const [seats, setSeats] = useState([]);
  const [selected, setSelected] = useState([]);

  const event = state?.event;

  useEffect(() => {
    if (!event) return;
    axios.get(`http://localhost:3000/api/seats/${event.id}/seats`)
      .then(res => setSeats(res.data.seats || []))
      .catch(err => console.error('Seat map error:', err));
  }, [event]);

  const handleSelect = (seat) => {
    setSelected(prev =>
      prev.includes(seat) ? prev.filter(s => s !== seat) : [...prev, seat]
    );
  };

  const handlePayment = () => {
    if (selected.length === 0) return alert("Select at least 1 seat");
    navigate('/payment', {
      state: {
        title: event.title,
        price: event.price,
        quantity: selected.length,
        selectedSeats: selected,
      }
    });
  };

  return (
    <div className="seat-selection-container">
      <h2>Select Seats for {event?.title}</h2>
      <div className="seat-grid">
        {seats.map((seat, index) => (
          <div
            key={index}
            className={`seat ${selected.includes(seat.seatNumber || seat.tier) ? 'selected' : ''}`}
            onClick={() => handleSelect(seat.seatNumber || seat.tier)}
          >
            {seat.seatNumber || seat.tier}
          </div>
        ))}
      </div>
      <button onClick={handlePayment} className="confirm-btn">
        Continue to Payment ({selected.length} seats)
      </button>
    </div>
  );
};

export default SeatSelectionPage;
