// src/pages/PaymentPage.js
import React from 'react';
import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import CheckoutForm from './CheckoutForm';
import { useLocation } from 'react-router-dom';
import './PaymentPage.css';

// Use your test publishable key
const stripePromise = loadStripe('pk_test_51Pdx...'); 

const PaymentPage = () => {
  const location = useLocation();
  const { title, price, quantity } = location.state || {};

  // Mock order summary calculations
  const subtotal = price * quantity || 7000;
  const tax = subtotal * 0.05 || 350;
  const total = subtotal + tax || 7350;

  return (
    <div className="payment-container">
      <h1>Checkout</h1>

      {/* Order Summary */}
      <section className="order-summary">
        <h2>Order Summary</h2>
        <div className="product-details">
          <div className="product-item">
            <img src="https://via.placeholder.com/60" alt="Event Poster" />
            <div className="product-info">
              <h3>{title || "Coldplay World Tour"}</h3>
              <p>Quantity: {quantity || 2}</p>
            </div>
            <div className="product-price">₹{subtotal}</div>
          </div>
        </div>
        <div className="total-section">
          <div className="total-row">
            <span>Subtotal</span>
            <span>₹{subtotal}</span>
          </div>
          <div className="total-row">
            <span>Tax (5%)</span>
            <span>₹{tax.toFixed(2)}</span>
          </div>
          <div className="total-row final">
            <span>Total</span>
            <span>₹{total.toFixed(2)}</span>
          </div>
        </div>
      </section>

      {/* Stripe Elements (Mock Mode) */}
      <Elements stripe={stripePromise}>
        <CheckoutForm />
      </Elements>
    </div>
  );
};

export default PaymentPage;
