import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SeatSelectionPage.css';

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state?.event;

  if (!event) {
    return <div className="error">Event data missing. Please go back.</div>;
  }

  const tiers = [
    { name: 'Silver', rows: ['A', 'B'], price: 200 },
    { name: 'Gold', rows: ['C', 'D'], price: 300 },
    { name: 'Platinum', rows: ['E', 'F'], price: 400 },
  ];

  const [selectedSeats, setSelectedSeats] = useState([]);

  const handleSelectSeat = (tierName, seat) => {
    const seatId = `${tierName}-${seat}`;
    setSelectedSeats(prev =>
      prev.includes(seatId)
        ? prev.filter(s => s !== seatId)
        : [...prev, seatId]
    );
  };

  const handleProceed = () => {
    const totalPrice = selectedSeats.reduce((sum, seatId) => {
      const tier = tiers.find(t => seatId.startsWith(t.name));
      return sum + (tier?.price || 0);
    }, 0);

    navigate('/payment', {
      state: {
        title: event.title,
        selectedSeats,
        quantity: selectedSeats.length,
        price: totalPrice / selectedSeats.length,
        totalPrice,
      },
    });
  };

  return (
    <div className="seat-selection-container">
      <h2>Select Seats for {event.title}</h2>
      <div className="screen">SCREEN</div>

      {tiers.map((tier) => (
        <div key={tier.name} className="tier-block">
          <h3>{tier.name} (â‚¹{tier.price})</h3>
          {tier.rows.map((row) => (
            <div key={row} className="seat-row">
              {[...Array(10)].map((_, i) => {
                const seat = `${row}${i + 1}`;
                const seatId = `${tier.name}-${seat}`;
                const isSelected = selectedSeats.includes(seatId);
                return (
                  <div
                    key={seatId}
                    className={`seat ${isSelected ? 'selected' : ''}`}
                    onClick={() => handleSelectSeat(tier.name, seat)}
                  >
                    {seat}
                  </div>
                );
              })}
            </div>
          ))}
        </div>
      ))}

      <div className="selection-summary">
        <p>Selected Seats: {selectedSeats.join(', ') || 'None'}</p>
        <button
          className="proceed-btn"
          onClick={handleProceed}
          disabled={selectedSeats.length === 0}
        >
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
