import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './EventsPage.css';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [filters, setFilters] = useState({
    date: '',
    language: '',
    category: '',
    price: ''
  });

  // Mock data for events
  useEffect(() => {
    const mockEvents = [
      {
        id: "1",
        name: "FINK India 2025 - Mumbai",
        date: "2025-04-03",
        time: "8:00 PM",
        location: "Antisocial - Lower Parel, Mumbai",
        image: "https://images.unsplash.com/photo-1529156069898-49953e39b3ac",
        description: "Folk, Indie | English | 21yrs+ | 4hrs",
        category: "Music Shows",
        language: "English",
        price: 999
      },
      {
        id: "2",
        name: "Pune Comedy Festival 2025",
        date: "2025-04-04",
        time: "7:00 PM",
        location: "Pune Theatre, Pune",
        image: "https://images.unsplash.com/photo-1515169067865-dae1abe0b6b3",
        description: "Comedy | Hindi | 18yrs+ | 3hrs",
        category: "Comedy Shows",
        language: "Hindi",
        price: 499
      },
      {
        id: "3",
        name: "Guns N' Roses - India Tour",
        date: "2025-05-17",
        time: "6:30 PM",
        location: "Mumbai Arena, Mumbai",
        image: "https://images.unsplash.com/photo-1519671482749-fd09be7ccebf",
        description: "Rock Music | English | All Ages | 2hrs",
        category: "Music Shows",
        language: "English",
        price: 1499
      },
      {
        id: "4",
        name: "All Hearts Tour - Shreya Ghoshal Live in Mumbai",
        date: "2025-05-10",
        time: "7:00 PM",
        location: "Mumbai Concert Hall, Mumbai",
        image: "https://images.unsplash.com/photo-1559628232-ded576a4adbc",
        description: "Bollywood Music | Hindi | All Ages | 3hrs",
        category: "Music Shows",
        language: "Hindi",
        price: 999
      }
    ];
    setEvents(mockEvents);
  }, []);

  // Filter events based on selected filters
  const filteredEvents = events.filter(event => {
    const matchesDate = !filters.date || event.date === filters.date;
    const matchesLanguage = !filters.language || event.language === filters.language;
    const matchesCategory = !filters.category || event.category === filters.category;
    const matchesPrice =
      !filters.price ||
      (filters.price === 'Below ₹500' && event.price <= 500) ||
      (filters.price === '₹501 - ₹1000' && event.price > 500 && event.price <= 1000) ||
      (filters.price === 'Above ₹1000' && event.price > 1000);

    return matchesDate && matchesLanguage && matchesCategory && matchesPrice;
  });

  return (
    <div className="events-page">
      {/* Filters Section */}
      <div className="filters-section">
        <h3>Filters</h3>

        {/* Date Filter */}
        <div className="filter-group">
          <h4>Date</h4>
          <button onClick={() => setFilters({ ...filters, date: '' })}>All Dates</button>
          <button onClick={() => setFilters({ ...filters, date: '2025-04-03' })}>Today</button>
          <button onClick={() => setFilters({ ...filters, date: '2025-04-04' })}>Tomorrow</button>
          <button onClick={() => setFilters({ ...filters, date: '' })}>This Weekend</button>
        </div>

        {/* Language Filter */}
        <div className="filter-group">
          <h4>Language</h4>
          <button onClick={() => setFilters({ ...filters, language: '' })}>All Languages</button>
          <button onClick={() => setFilters({ ...filters, language: 'English' })}>English</button>
          <button onClick={() => setFilters({ ...filters, language: 'Hindi' })}>Hindi</button>
        </div>

        {/* Category Filter */}
        <div className="filter-group">
          <h4>Category</h4>
          <button onClick={() => setFilters({ ...filters, category: '' })}>All Categories</button>
          <button onClick={() => setFilters({ ...filters, category: 'Music Shows' })}>Music Shows</button>
          <button onClick={() => setFilters({ ...filters, category: 'Comedy Shows' })}>Comedy Shows</button>
          {/* Add more categories as needed */}
        </div>

        {/* Price Filter */}
        <div className="filter-group">
          <h4>Price</h4>
          <button onClick={() => setFilters({ ...filters, price: '' })}>All Prices</button>
          <button onClick={() => setFilters({ ...filters, price: 'Below ₹500' })}>Below ₹500</button>
          <button onClick={() => setFilters({ ...filters, price: '₹501 - ₹1000' })}>₹501 - ₹1000</button>
          <button onClick={() => setFilters({ ...filters, price: 'Above ₹1000' })}>Above ₹1000</button>
        </div>
      </div>

      {/* Events Section */}
      <div className="events-container">
        <h2>Events In Your City</h2>

        {/* Event Cards */}
        <div className="events-grid">
          {filteredEvents.map(event => (
            <Link to={`/events/${event.id}`} key={event.id} className="event-card">
              <img src={event.image} alt={event.name} className="event-image" />
              <div className="event-info">
                <h3>{event.name}</h3>
                <p>{new Date(event.date).toLocaleDateString()}</p>
                <p>{event.location}</p>
                {/* Book Now Button */}
                <Link to={`/book/${event.id}`} className="book-now-button">Book Now</Link>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default EventsPage;
