import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SeatSelectionPage.css';

const SeatSelectionPage = () => {
  const { state } = useLocation();
  const navigate = useNavigate();

  const { title, price, eventId } = state || {};
  const [selectedSeats, setSelectedSeats] = useState([]);

  const rows = ['A', 'B', 'C', 'D', 'E', 'F'];
  const seatsPerRow = 8;

  const seatPrice = (row) => {
    if (['A', 'B'].includes(row)) return price * 1.5; // Premium
    if (['C', 'D'].includes(row)) return price;       // Standard
    return price * 0.75;                              // Economy
  };

  const toggleSeat = (seat) => {
    setSelectedSeats((prev) =>
      prev.includes(seat) ? prev.filter((s) => s !== seat) : [...prev, seat]
    );
  };

  const getTotal = () =>
    selectedSeats.reduce((total, seat) => {
      const row = seat.charAt(0);
      return total + seatPrice(row);
    }, 0);

  const handleContinue = () => {
    if (selectedSeats.length === 0) {
      alert('Please select at least one seat.');
      return;
    }
    navigate('/payment', {
      state: {
        title,
        price: price,
        quantity: selectedSeats.length,
        selectedSeats,
        totalAmount: getTotal(),
      },
    });
  };

  if (!title || !price) {
    return <div className="error">Event data missing. Please go back.</div>;
  }

  return (
    <div className="seat-selection-container">
      <h2>Select Seats for {title}</h2>
      <div className="seat-grid">
        {rows.map((row) => (
          <div key={row} className="seat-row">
            {Array.from({ length: seatsPerRow }, (_, i) => {
              const seatId = `${row}${i + 1}`;
              return (
                <div
                  key={seatId}
                  className={`seat ${selectedSeats.includes(seatId) ? 'selected' : ''}`}
                  onClick={() => toggleSeat(seatId)}
                  title={`₹${seatPrice(row)}`}
                >
                  {seatId}
                </div>
              );
            })}
          </div>
        ))}
      </div>
      <div className="seat-summary">
        <p>Selected Seats: {selectedSeats.join(', ') || 'None'}</p>
        <p>Total Amount: ₹{getTotal().toFixed(2)}</p>
        <button onClick={handleContinue} className="continue-btn">
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
