// src/pages/EventDetailsPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

import BookingModal from '../components/BookingModal';
import './EventDetailsPage.css';

const EventDetailsPage = () => {
  const { eventId } = useParams();
  const navigate = useNavigate();
  const [event, setEvent] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const mockEvents = {
      "coldplay-2025": {
        id: "coldplay-2025",
        type: "concert",
        title: "Coldplay: Music of the Spheres",
        poster: "https://images.unsplash.com/photo-1475721027785-f74eccf877e2?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&q=80&w=800",
        date: "2025-04-15",
        time: "7:30 PM",
        price: 3500,
        location: "DY Patil Stadium, Mumbai",
        details: {
          artists: ["Coldplay"],
          sponsors: ["Coca-Cola", "Visa"],
          duration: "2h 30m"
        }
      },
      "avengers-endgame": {
        id: "avengers-endgame",
        type: "movie",
        title: "Avengers: Endgame",
        poster: "https://images.unsplash.com/photo-1552820728-8b83bb6b773f?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&q=80&w=800",
        date: "2025-05-01",
        time: "6:00 PM",
        price: 300,
        location: "PVR Cinemas, Mumbai",
        details: {
          cast: ["Robert Downey Jr.", "Chris Evans", "Scarlett Johansson"],
          director: "Anthony & Joe Russo",
          duration: "3h 1m",
          theater: "Screen 5"
        }
      }
    };

    setEvent(mockEvents[eventId]);
  }, [eventId]);

  if (!event) return <div className="loading">Loading...</div>;

  return (
    <div className="event-details-page">
      <div className="event-hero" style={{ backgroundImage: `url(${event.poster})` }}>
        <div className="hero-overlay">
          <h1 className="event-title">{event.title}</h1>
        </div>
      </div>

      <div className="event-content">
        <div className="event-meta-grid">
          <div className="meta-item">
            <span className="meta-label">Date & Time</span>
            <span className="meta-value">
              {new Date(event.date).toLocaleDateString('en-IN')} • {event.time}
            </span>
          </div>
          <div className="meta-item">
            <span className="meta-label">Location</span>
            <span className="meta-value">{event.location}</span>
          </div>
          <div className="meta-item">
            <span className="meta-label">Duration</span>
            <span className="meta-value">{event.details.duration}</span>
          </div>
        </div>

        {event.type === 'concert' && (
          <div className="details-section">
            <h2>Concert Details</h2>
            <div className="detail-item">
              <span>Artists:</span>
              <div className="artist-list">
                {event.details.artists.map((artist, i) => (
                  <div key={i} className="artist-tag">{artist}</div>
                ))}
              </div>
            </div>
            <div className="detail-item">
              <span>Sponsors:</span>
              <div className="sponsor-list">
                {event.details.sponsors.map((sponsor, i) => (
                  <div key={i} className="sponsor-tag">{sponsor}</div>
                ))}
              </div>
            </div>
          </div>
        )}

        {event.type === 'movie' && (
          <div className="details-section">
            <h2>Movie Details</h2>
            <div className="detail-item">
              <span>Cast:</span>
              <div className="cast-list">
                {event.details.cast.map((actor, i) => (
                  <div key={i} className="cast-tag">{actor}</div>
                ))}
              </div>
            </div>
            <div className="detail-item">
              <span>Theater:</span>
              <span className="theater">{event.details.theater}</span>
            </div>
          </div>
        )}

        <div className="booking-cta">
          <div className="price-section">
            <span className="price-label">Starting from</span>
            <span className="price">₹{event.price}</span>
          </div>
          <button className="book-button" onClick={() => setShowModal(true)}>
            Book Tickets
          </button>
        </div>
      </div>

      {showModal && <BookingModal event={event} onClose={() => setShowModal(false)} />}
    </div>
  );
};

export default EventDetailsPage;
