// src/pages/EventsPage.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import EventCard from '../components/EventCard';
import './EventsPage.css';

const EventsPage = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const mockEvents = [
      {
        id: "coldplay-concert",
        type: "concert",
        title: "Coldplay World Tour 2025",
        date: "2025-04-15",
        price: 5000,
        location: "Mumbai",
        poster: "https://images.unsplash.com/photo-1514539079130-25950c84af69?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
        details: {
          artists: ["Coldplay"],
          sponsors: ["Coca-Cola", "Visa"],
          duration: "2h 30m"
        }
      },
      {
        id: "avengers-endgame",
        type: "movie",
        title: "Avengers: Endgame",
        date: "2025-05-01",
        price: 300,
        location: "Delhi",
        poster: "https://images.unsplash.com/photo-1552820728-8b83bb6b773f?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
        details: {
          cast: ["Robert Downey Jr.", "Chris Evans"],
          director: "Russo Brothers",
          duration: "3h 1m"
        }
      }
    ];
    setEvents(mockEvents);
  }, []);

  return (
    <>
      <Header />
      <div className="events-container">
        <h1 className="page-title">Events in Mumbai</h1>
        <div className="events-grid">
          {events.map(event => (
            <EventCard key={event.id} event={event} />
          ))}
        </div>
      </div>
    </>
  );
};

export default EventsPage;
