// src/pages/EventDetailsPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import BookingModal from '../components/BookingModal';
import './EventDetailsPage.css';

const EventDetailsPage = () => {
  const { eventId } = useParams();
  const [event, setEvent] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    // Mock API call
    const mockEvent = {
      id: 1,
      title: "Coldplay Concert",
      type: "concert",
      date: "2025-06-15",
      time: "7:30 PM",
      price: 5000,
      location: "Mumbai",
      language: "English",
      genre: "Rock",
      venue: "DY Patil Stadium",
      poster: "https://source.unsplash.com/random/1600x900?concert",
      details: {
        artists: ["Coldplay"],
        sponsors: ["Coca-Cola"],
        duration: "2h 30m",
        organizer: "Live Nation"
      }
    };
    setEvent(mockEvent);
  }, [eventId]);

  const renderEventSpecificDetails = () => {
    if (!event) return null;

    switch(event.type) {
      case 'movie':
        return (
          <div className="details-section">
            <h2>Movie Details</h2>
            <div className="detail-item">
              <span>Cast:</span>
              <div className="cast-list">
                {event.details.cast?.map((actor, i) => (
                  <span key={i} className="info-tag">{actor}</span>
                ))}
              </div>
            </div>
            <div className="detail-item">
              <span>Theater:</span>
              <span className="theater-info">{event.venue}</span>
            </div>
          </div>
        );

      case 'concert':
        return (
          <div className="details-section">
            <h2>Concert Details</h2>
            <div className="detail-item">
              <span>Artists:</span>
              <div className="artist-list">
                {event.details.artists?.map((artist, i) => (
                  <span key={i} className="info-tag">{artist}</span>
                ))}
              </div>
            </div>
            <div className="detail-item">
              <span>Sponsors:</span>
              <div className="sponsor-list">
                {event.details.sponsors?.map((sponsor, i) => (
                  <span key={i} className="info-tag">{sponsor}</span>
                ))}
              </div>
            </div>
          </div>
        );

      default:
        return (
          <div className="details-section">
            <h2>Event Details</h2>
            <div className="detail-item">
              <span>Organizers:</span>
              <span className="organizers">{event.details.organizer}</span>
            </div>
          </div>
        );
    }
  };

  return (
    <div className="event-details-page">
      {/* ... (keep previous structure) */}
      {renderEventSpecificDetails()}
      {/* ... (rest of the component) */}
    </div>
  );
};

export default EventDetailsPage;
