// src/pages/EventsPage.js
import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import EventCard from '../components/EventCard';
import './EventsPage.css';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [filters, setFilters] = useState({
    date: '',
    location: '',
    category: 'all',
    priceRange: [0, 5000]
  });

  const [currentPage, setCurrentPage] = useState(1);
  const eventsPerPage = 20;

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await fetch(
          `http://localhost:5000/api/events?page=${currentPage}&limit=${eventsPerPage}`
        );
        const data = await response.json();
        setEvents(data.events || []);
      } catch (error) {
        console.error('Error fetching events:', error);
      }
    };
    fetchEvents();
  }, [currentPage]);

  const filteredEvents = events.filter(event => {
    const matchesDate = !filters.date || event.eventDate.startsWith(filters.date);
    const matchesLocation = !filters.location || event.location === filters.location;
    const matchesCategory =
      filters.category === 'all' || event.type.toLowerCase() === filters.category;
    const matchesPrice =
      event.price >= filters.priceRange[0] && event.price <= filters.priceRange[1];

    return matchesDate && matchesLocation && matchesCategory && matchesPrice;
  });

  return (
    <div className="events-page">
      <Header />

      {/* Filters */}
      <div className="filters-container">
        <div className="filter-group">
          <label>Date</label>
          <input
            type="date"
            value={filters.date}
            onChange={(e) => setFilters({ ...filters, date: e.target.value })}
          />
        </div>

        <div className="filter-group">
          <label>Location</label>
          <select
            value={filters.location}
            onChange={(e) => setFilters({ ...filters, location: e.target.value })}
          >
            <option value="">All Locations</option>
            <option value="Mumbai">Mumbai</option>
            <option value="Delhi">Delhi</option>
            <option value="Bengaluru">Bengaluru</option>
          </select>
        </div>

        <div className="filter-group">
          <label>Category</label>
          <select
            value={filters.category}
            onChange={(e) => setFilters({ ...filters, category: e.target.value })}
          >
            <option value="all">All Events</option>
            <option value="concert">Concerts</option>
            <option value="movie">Movies</option>
            <option value="theatre">Theatre</option>
          </select>
        </div>

        <div className="filter-group">
          <label>Price Range</label>
          <input
            type="range"
            min="0"
            max="5000"
            value={filters.priceRange[1]}
            onChange={(e) =>
              setFilters({ ...filters, priceRange: [0, parseInt(e.target.value)] })
            }
          />
          <div className="price-range">₹0 - ₹{filters.priceRange[1]}</div>
        </div>
      </div>

      {/* Events Grid */}
      <div className="events-grid">
        {filteredEvents.map(event => (
          <EventCard key={event.eventId} event={event} />
        ))}
      </div>

      {/* Pagination */}
      <div className="pagination">
        {Array.from({ length: Math.ceil(400 / eventsPerPage) }).map((_, index) => (
          <button
            key={index}
            onClick={() => setCurrentPage(index + 1)}
            className={currentPage === index + 1 ? 'active' : ''}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

export default EventsPage;
