import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import EventCard from '../components/EventCard';
import './EventsPage.css';

const EventsPage = () => {
  const [allEvents, setAllEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [filters, setFilters] = useState({
    date: '',
    location: 'Mumbai',
    category: 'all',
    priceRange: [0, 5000],
  });
  const [currentPage, setCurrentPage] = useState(1);
  const eventsPerPage = 12;

  useEffect(() => {
    fetch('http://localhost:3000/api/events')
      .then((res) => res.json())
      .then((data) => {
        if (data.events) {
          const transformedEvents = data.events.map((event) => {
            let type = 'other';
            if (event.type === 'MovieSchema') type = 'movie';
            else if (event.type === 'ConcertSchema') type = 'concert';
            else if (event.type === 'TheatreSchema' || event.type === 'Theatre') type = 'theatre';

            return {
              id: event.eventId,
              title: event.name,
              date: event.eventDate,
              poster: event.posterImage || 'https://via.placeholder.com/150',
              type,
              price: 0,
              location: 'Mumbai',
            };
          });
          setAllEvents(transformedEvents);
        }
      })
      .catch((err) => console.error('Error fetching events:', err));
  }, []);

  useEffect(() => {
    const filtered = allEvents.filter((event) => {
      const matchesDate = !filters.date ||
        new Date(event.date).toISOString().split('T')[0] === filters.date;
      const matchesLocation = event.location === filters.location;
      const matchesCategory = filters.category === 'all' || event.type === filters.category;
      const matchesPrice = event.price >= filters.priceRange[0] && event.price <= filters.priceRange[1];

      return matchesDate && matchesLocation && matchesCategory && matchesPrice;
    });
    setFilteredEvents(filtered);
    setCurrentPage(1); // Reset page when filters change
  }, [allEvents, filters]);

  // Pagination logic
  const indexOfLast = currentPage * eventsPerPage;
  const indexOfFirst = indexOfLast - eventsPerPage;
  const currentEvents = filteredEvents.slice(indexOfFirst, indexOfLast);
  const totalPages = Math.ceil(filteredEvents.length / eventsPerPage);

  const handlePageChange = (pageNum) => setCurrentPage(pageNum);

  return (
    <div className="events-page">
      <Header />

      <div className="filters-bar">
        <div className="filter-group">
          <label>Date</label>
          <input
            type="date"
            value={filters.date}
            onChange={(e) => setFilters({ ...filters, date: e.target.value })}
          />
        </div>

        <div className="filter-group">
          <label>Location</label>
          <select
            value={filters.location}
            onChange={(e) => setFilters({ ...filters, location: e.target.value })}
          >
            <option value="Mumbai">Mumbai</option>
            <option value="Delhi">Delhi</option>
            <option value="Bengaluru">Bengaluru</option>
          </select>
        </div>

        <div className="filter-group">
          <label>Category</label>
          <select
            value={filters.category}
            onChange={(e) => setFilters({ ...filters, category: e.target.value })}
          >
            <option value="all">All Events</option>
            <option value="concert">Concerts</option>
            <option value="movie">Movies</option>
            <option value="theatre">Theatre</option>
          </select>
        </div>

        <div className="filter-group">
          <label>Max Price</label>
          <input
            type="range"
            min="0"
            max="5000"
            value={filters.priceRange[1]}
            onChange={(e) => setFilters({ ...filters, priceRange: [0, Number(e.target.value)] })}
          />
          <span>₹0 - ₹{filters.priceRange[1]}</span>
        </div>
      </div>

      <div className="events-grid">
        {currentEvents.map((event) => (
          <EventCard key={event.id} event={event} />
        ))}
      </div>

      {totalPages > 1 && (
        <div className="pagination">
          {Array.from({ length: totalPages }, (_, idx) => (
            <button
              key={idx}
              className={currentPage === idx + 1 ? 'active' : ''}
              onClick={() => handlePageChange(idx + 1)}
            >
              {idx + 1}
            </button>
          ))}
        </div>
      )}
    </div>
  );
};

export default EventsPage;
