import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import EventCard from '../components/EventCard';
import './EventsPage.css';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [priceFilters, setPriceFilters] = useState([]);
  const [isDateExpanded, setIsDateExpanded] = useState(true);
  const [isLanguageExpanded, setIsLanguageExpanded] = useState(false);
  const [isCategoriesExpanded, setIsCategoriesExpanded] = useState(false);
  const [isPriceExpanded, setIsPriceExpanded] = useState(false);

  const categoryOptions = [
    "Workshops", "Music Shows", "Comedy Shows", "Performances", 
    "Kids", "Meetups", "Screening", "Exhibitions", "Talks"
  ];

  // Mock data for events
  useEffect(() => {
    const mockEvents = [
      {
        id: "1",
        name: "FINK India 2025 - Mumbai",
        date: "2025-04-03",
        time: "8:00 PM",
        location: "Antisocial - Lower Parel",
        image: "https://images.unsplash.com/photo-1529156069898-49953e39b3ac",
        description: "Folk, Indie | English | 21yrs+ | 4hrs",
        category: "Music Shows",
        language: "English",
        price: 999,
        isPromoted: true
      },
      {
        id: "2",
        name: "Pune Comedy Festival 2025",
        date: "2025-04-04",
        time: "7:00 PM",
        location: "Pune Convention Center",
        image: "https://images.unsplash.com/photo-1515169067865-dae1abe0b6b3",
        description: "Stand-up Comedy | Hindi | All Ages | 2hrs",
        category: "Comedy Shows",
        language: "Hindi",
        price: 499,
        isPromoted: true
      },
      {
        id: "3",
        name: "Guns N' Roses - India Tour 2025",
        date: "2025-05-17",
        time: "6:00 PM",
        location: "Wankhede Stadium, Mumbai",
        image: "https://images.unsplash.com/photo-1519671482749-fd09be7ccebf",
        description: "Rock Concert | English | All Ages | 3hrs",
        category: "Music Shows",
        language: "English",
        price: 1999
      },
      {
        id: "4",
        name: "All Hearts Tour - Shreya Ghoshal Live",
        date: "2025-05-10",
        time: "7:30 PM",
        location: "Jio World Garden, Mumbai",
        image: "https://images.unsplash.com/photo-1559628232-ded576a4adbc",
        description: "Bollywood Music | Hindi | All Ages | 2hrs",
        category: "Music Shows",
        language: "Hindi",
        price: 1499
      }
    ];
    setEvents(mockEvents);
  }, []);

  // Filter events based on selected filters
  const filteredEvents = events.filter(event => {
    let matchesDate = true;
    let matchesLanguage = true;
    let matchesCategory = true;
    let matchesPrice = true;

    if (selectedDate) {
      const eventDate = new Date(event.date);
      const today = new Date();

      if (selectedDate === 'Today') {
        matchesDate = eventDate.toDateString() === today.toDateString();
      } else if (selectedDate === 'Tomorrow') {
        const tomorrow = new Date();
        tomorrow.setDate(today.getDate() + 1);
        matchesDate = eventDate.toDateString() === tomorrow.toDateString();
      } else if (selectedDate === 'This Weekend') {
        const friday = new Date();
        friday.setDate(today.getDate() + (5 - today.getDay()));
        const sunday = new Date();
        sunday.setDate(friday.getDate() + 2);
        matchesDate = eventDate >= friday && eventDate <= sunday;
      }
    }

    if (selectedLanguage && selectedLanguage !== '') {
      matchesLanguage = event.language === selectedLanguage;
    }

    if (selectedCategory && selectedCategory !== '') {
      matchesCategory = event.category === selectedCategory;
    }

    if (priceFilters.length > 0) {
      matchesPrice = false;
      for (const range of priceFilters) {
        if (range === 'Free') {
          if (event.price === 0) {
            matchesPrice = true;
            break;
          }
        } else if (range === '0-500') {
          if (event.price >= 0 && event.price <= 500) {
            matchesPrice = true;
            break;
          }
        } else if (range === '501-2000') {
          if (event.price > 500 && event.price <= 2000) {
            matchesPrice = true;
            break;
          }
        } else if (range === 'Above 2000') {
          if (event.price > 2000) {
            matchesPrice = true;
            break;
          }
        }
      }
    }

    return matchesDate && matchesLanguage && matchesCategory && matchesPrice;
  });

  const togglePriceFilter = (range) => {
    if (priceFilters.includes(range)) {
      setPriceFilters(priceFilters.filter(r => r !== range));
    } else {
      setPriceFilters([...priceFilters, range]);
    }
  };

  return (
    <div className="events-page">
      {/* Header */}
      <Header />

      <div className="main-content">
        <div className="container">
          <div className="page-title-section">
            <h1>Events In Mumbai</h1>
          </div>

          {/* Category filter buttons (horizontal) */}
          <div className="category-buttons">
            {categoryOptions.map(category => (
              <button 
                key={category}
                className={selectedCategory === category ? 'active' : ''}
                onClick={() => setSelectedCategory(selectedCategory === category ? '' : category)}
              >
                {category}
              </button>
            ))}
          </div>

          <div className="content-container">
            {/* Left sidebar with filters */}
            <div className="filters-sidebar">
              <h2>Filters</h2>

              {/* Date Filter */}
              <div className="filter-section">
                <div 
                  className="filter-header" 
                  onClick={() => setIsDateExpanded(!isDateExpanded)}
                >
                  <h3>Date</h3>
                  <span>{isDateExpanded ? '−' : '+'}</span>
                </div>

                {isDateExpanded && (
                  <div className="filter-options">
                    <div className="date-buttons">
                      <button 
                        className={selectedDate === 'Today' ? 'active' : ''}
                        onClick={() => setSelectedDate(selectedDate === 'Today' ? '' : 'Today')}
                      >
                        Today
                      </button>
                      <button 
                        className={selectedDate === 'Tomorrow' ? 'active' : ''}
                        onClick={() => setSelectedDate(selectedDate === 'Tomorrow' ? '' : 'Tomorrow')}
                      >
                        Tomorrow
                      </button>
                      <button 
                        className={selectedDate === 'This Weekend' ? 'active' : ''}
                        onClick={() => setSelectedDate(selectedDate === 'This Weekend' ? '' : 'This Weekend')}
                      >
                        This Weekend
                      </button>
                    </div>
                    <div className="date-range">
                      <label>
                        <input type="checkbox" /> Date Range
                      </label>
                    </div>
                  </div>
                )}
              </div>

              {/* Languages Filter */}
              <div className="filter-section">
                <div 
                  className="filter-header" 
                  onClick={() => setIsLanguageExpanded(!isLanguageExpanded)}
                >
                  <h3>Languages</h3>
                  <span>{isLanguageExpanded ? '−' : '+'}</span>
                </div>

                {isLanguageExpanded && (
                  <div className="filter-options">
                    <button 
                      className={selectedLanguage === 'English' ? 'active' : ''}
                      onClick={() => setSelectedLanguage(selectedLanguage === 'English' ? '' : 'English')}
                    >
                      English
                    </button>
                    <button 
                      className={selectedLanguage === 'Hindi' ? 'active' : ''}
                      onClick={() => setSelectedLanguage(selectedLanguage === 'Hindi' ? '' : 'Hindi')}
                    >
                      Hindi
                    </button>
                    {/* Add more languages as needed */}
                  </div>
                )}
              </div>

              {/* Categories Filter */}
              <div className="filter-section">
                <div 
                  className="filter-header" 
                  onClick={() => setIsCategoriesExpanded(!isCategoriesExpanded)}
                >
                  <h3>Categories</h3>
                  <span>{isCategoriesExpanded ? '−' : '+'}</span>
                </div>

                {isCategoriesExpanded && (
                  <div className="filter-options">
                    {categoryOptions.map(category => (
                      <button 
                        key={category}
                        className={selectedCategory === category ? 'active' : ''}
                        onClick={() => setSelectedCategory(selectedCategory === category ? '' : category)}
                      >
                        {category}
                      </button>
                    ))}
                  </div>
                )}
              </div>

              {/* Price Filter */}
              <div className="filter-section">
                <div 
                  className="filter-header" 
                  onClick={() => setIsPriceExpanded(!isPriceExpanded)}
                >
                  <h3>Price</h3>
                  <span>{isPriceExpanded ? '−' : '+'}</span>
                </div>

                {isPriceExpanded && (
                  <div className="filter-options">
                    <label className="checkbox-label">
                      <input 
                        type="checkbox" 
                        checked={priceFilters.includes('Free')}
                        onChange={() => togglePriceFilter('Free')}
                      /> 
                      Free
                    </label>
                    <label className="checkbox-label">
                      <input 
                        type="checkbox" 
                        checked={priceFilters.includes('0-500')}
                        onChange={() => togglePriceFilter('0-500')}
                      /> 
                      0-500
                    </label>
                    <label className="checkbox-label">
                      <input 
                        type="checkbox" 
                        checked={priceFilters.includes('501-2000')}
                        onChange={() => togglePriceFilter('501-2000')}
                      /> 
                      501-2000
                    </label>
                    <label className="checkbox-label">
                      <input 
                        type="checkbox" 
                        checked={priceFilters.includes('Above 2000')}
                        onChange={() => togglePriceFilter('Above 2000')}
                      /> 
                      Above 2000
                    </label>
                  </div>
                )}
              </div>

              <button className="browse-venues-btn">Browse by Venues</button>
            </div>

            {/* Events Grid */}
            <div className="events-grid">
              {filteredEvents.length > 0 ? (
                filteredEvents.map(event => (
                  <EventCard key={event.id} event={event} />
                ))
              ) : (
                <div className="no-events">No events match your filters</div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventsPage;
