// src/components/EventCard.js
import React from 'react';
import { Link } from 'react-router-dom';
import './EventCard.css';

const EventCard = ({ event }) => {
  return (
    <Link to={`/events/${event.id}`} className="event-card">
      <div className="card-image-container">
        <img src={event.poster} alt={event.title} className="event-image" />
        <div className="event-badge">{event.type}</div>
      </div>
      <div className="card-content">
        <h3 className="event-title">{event.title}</h3>
        <div className="event-meta">
          <span className="event-genre">{event.genre}</span>
          <span className="event-language">{event.language}</span>
        </div>
        <div className="event-details">
          <span className="event-date">{new Date(event.date).toLocaleDateString()}</span>
          <span className="event-price">â‚¹{event.price}</span>
        </div>
      </div>
    </Link>
  );
};

export default EventCard;
