import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './EventDetailsPage.css';

const EventDetailsPage = () => {
  const { eventId } = useParams();
  const [event, setEvent] = useState(null);

  useEffect(() => {
    // Mock data for events
    const mockEvents = [
      {
        id: "1",
        name: "Summer Music Festival",
        date: "2025-04-15",
        time: "6:00 PM",
        location: "Central Park, New York",
        image: "https://images.unsplash.com/photo-1529156069898-49953e39b3ac",
        description: "Join us for an unforgettable night of live music.",
        category: "Music Shows",
        price: "999"
      },
      {
        id: "2",
        name: "Winter Wonderland Concert",
        date: "2025-12-20",
        time: "7:30 PM",
        location: "Madison Square Garden, New York",
        image: "https://images.unsplash.com/photo-1515169067865-dae1abe0b6b3",
        description: "Experience the magic of winter with live music.",
        category: "Music Shows",
        price: "1499"
      },
      {
        id: "3",
        name: "Comedy Night Extravaganza",
        date: "2025-05-10",
        time: "8:00 PM",
        location: "Comedy Club, Los Angeles",
        image: "https://images.unsplash.com/photo-1519671482749-fd09be7ccebf",
        description: "Laugh out loud with the best comedians in town.",
        category: "Comedy Shows",
        price: "799"
      },
      {
        id: "4",
        name: "Theatre Workshop",
        date: "2025-06-25",
        time: "10:00 AM",
        location: "Downtown Theatre, Chicago",
        image: "https://images.unsplash.com/photo-1559628232-ded576a4adbc",
        description: "Learn acting techniques from industry professionals.",
        category: "Workshops",
        price: "499"
      }
    ];

    const foundEvent = mockEvents.find(e => e.id === eventId);
    setEvent(foundEvent);
  }, [eventId]);

  if (!event) return <div className="loading">Loading...</div>;

  return (
    <div className="event-details-page">
      {/* Event Banner */}
      <div className="event-banner">
        <img src={event.image} alt={event.name} />
      </div>

      <div className="event-details-container">
        {/* Left Content - Event Information */}
        <div className="event-info-section">
          <h1>{event.name}</h1>
          <div className="event-meta">
            <span className="event-category">{event.category}</span>
            <span className="event-duration">2hrs</span>
          </div>

          <div className="event-schedule">
            <div className="event-date-time">
              <h3>{new Date(event.date).toDateString()} • {event.time}</h3>
              <p>{event.location}</p>
            </div>
          </div>

          <div className="event-description">
            <h3>About</h3>
            <p>{event.description}</p>
          </div>
        </div>

        {/* Right Content - Booking Section */}
        <div className="booking-section">
          <div className="ticket-price">
            <p>Tickets from</p>
            <h3>₹ {event.price}</h3>
          </div>

          <button className="book-button">Book</button>

          <div className="share-section">
            <h4>Share this event</h4>
            <div className="share-buttons">
              <button>Facebook</button>
              <button>Twitter</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventDetailsPage;
