import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './PaymentPage.css';

const PaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { title, price, quantity, eventId } = location.state || {};

  // Required: Redirect back if essential data is missing
  if (!title || !price || !quantity) {
    return <div className="error">Missing event info. Please go back and select tickets again.</div>;
  }

  const subtotal = price * quantity;
  const tax = subtotal * 0.05;
  const total = subtotal + tax;

  const [email, setEmail] = useState('');
  const [errors, setErrors] = useState({});
  const [isProcessing, setIsProcessing] = useState(false);

  const validateForm = () => {
    const newErrors = {};
    if (!email) newErrors.email = 'Email is required';
    else if (!/\S+@\S+\.\S+/.test(email)) newErrors.email = 'Email is invalid';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setIsProcessing(true);
    try {
      const res = await fetch('http://localhost:3000/api/payments/checkout', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          customerEmail: email,
          items: [
            {
              name: title,
              amount: price,
              currency: 'inr',
              quantity: quantity,
              description: `Booking for ${title}`,
            },
          ],
        }),
      });

      const data = await res.json();
      if (data.sessionId) {
        window.location.href = `https://checkout.stripe.com/pay/${data.sessionId}`;
      } else {
        throw new Error(data.message || 'Payment failed');
      }
    } catch (err) {
      console.error(err);
      alert('Something went wrong. Please try again.');
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="checkout-container">
      <form className="checkout-form-section" onSubmit={handleSubmit}>
        <h2>Confirm Your Booking</h2>
        <div className="form-group">
          <label>Email for Tickets</label>
          <input
            type="email"
            placeholder="you@example.com"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className={errors.email ? 'error' : ''}
          />
          {errors.email && <div className="error-message">{errors.email}</div>}
        </div>

        <div className="summary-totals">
          <div><strong>Event:</strong> {title}</div>
          <div><strong>Price per Ticket:</strong> â‚¹{price}</div>
          <div><strong>Quantity:</strong> {quantity}</div>
          <div><strong>Subtotal:</strong> â‚¹{subtotal.toFixed(2)}</div>
          <div><strong>Tax (5%):</strong> â‚¹{tax.toFixed(2)}</div>
          <div><strong>Total:</strong> â‚¹{total.toFixed(2)}</div>
        </div>

        <button type="submit" className="pay-button" disabled={isProcessing}>
          {isProcessing ? 'Redirecting to Stripe...' : `Pay â‚¹${total.toFixed(2)}`}
        </button>
      </form>
    </div>
  );
};

export default PaymentPage;
