// src/pages/PaymentPage.js
import React from 'react';
import './PaymentPage.css';

const PaymentPage = () => {
  return (
    <div className="payment-page">
      <h1>Checkout</h1>

      {/* Express Checkout */}
      <section className="express-checkout">
        <h2>Express Checkout</h2>
        {/* Placeholder for Stripe Express Checkout */}
        <button className="stripe-checkout-button">Pay with Stripe</button>
      </section>

      {/* Order Summary */}
      <section className="order-summary">
        <h2>Order Summary</h2>
        <p>Event: Coldplay World Tour</p>
        <p>Tickets: 2</p>
        <p>Total Price: â‚¹7000</p>
      </section>

      {/* Email Input */}
      <section className="email-section">
        <h2>Email Address</h2>
        <input type="email" placeholder="Enter your email" />
      </section>

      {/* Payment Methods */}
      <section className="payment-methods">
        <h2>Payment Methods</h2>
        {/* Placeholder for Stripe Payment Methods */}
        <button className="stripe-payment-button">Pay Now</button>
      </section>
    </div>
  );
};

export default PaymentPage;
