// src/pages/ConcertSeatSelectionPage.js
import React, { useState } from 'react';
import './ConcertSeatSelectionPage.css';
import { useLocation, useNavigate } from 'react-router-dom';

const ConcertSeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { event } = location.state || {};
  const [selectedSeats, setSelectedSeats] = useState([]);

  const tiers = [
    { label: 'Platinum', rows: ['A', 'B'], price: 600, color: 'platinum' },
    { label: 'Gold', rows: ['C', 'D', 'E'], price: 400, color: 'gold' },
    { label: 'Silver', rows: ['F', 'G', 'H'], price: 250, color: 'silver' },
  ];

  const seatsPerRow = 22;

  const toggleSeat = (seat) => {
    setSelectedSeats((prev) =>
      prev.includes(seat) ? prev.filter((s) => s !== seat) : [...prev, seat]
    );
  };

  const getPrice = (row) => {
    const tier = tiers.find((t) => t.rows.includes(row));
    return tier ? tier.price : 0;
  };

  const getColorClass = (row) => {
    const tier = tiers.find((t) => t.rows.includes(row));
    return tier ? tier.color : '';
  };

  const handleProceed = () => {
    if (selectedSeats.length === 0) {
      alert("Please select at least one seat.");
      return;
    }

    navigate('/payment', {
      state: {
        title: event?.title || "Concert",
        quantity: selectedSeats.length,
        price: getPrice(selectedSeats[0].charAt(0)), // assuming same tier
        selectedSeats,
      }
    });
  };

  return (
    <div className="concert-seat-container">
      <h2>Select Seats for {event?.title || "Concert"}</h2>

      <div className="stage">STAGE</div>

      <div className="seat-map">
        {tiers.map((tier) =>
          tier.rows.map((row) => (
            <div className="seat-row" key={row}>
              <div className="row-label">{row}</div>
              {Array.from({ length: seatsPerRow }, (_, i) => {
                const seatId = `${row}${i + 1}`;
                return (
                  <div
                    key={seatId}
                    className={`seat ${selectedSeats.includes(seatId) ? 'selected' : ''} ${getColorClass(row)}`}
                    onClick={() => toggleSeat(seatId)}
                  >
                    {i + 1}
                  </div>
                );
              })}
            </div>
          ))
        )}
      </div>

      <div className="summary">
        <p>Selected: {selectedSeats.join(', ') || 'None'}</p>
        <p>Total: â‚¹{selectedSeats.reduce((sum, seat) => sum + getPrice(seat.charAt(0)), 0)}</p>
        <button className="proceed-btn" onClick={handleProceed}>
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default ConcertSeatSelectionPage;
