import React, { useState } from 'react';
import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import CheckoutForm from './CheckoutForm';
import { useLocation } from 'react-router-dom';
import './PaymentPage.css';

// Load Stripe with your publishable key
const stripePromise = loadStripe('your-publishable-key-here');

const PaymentPage = () => {
  const location = useLocation();
  const { title, price, quantity } = location.state || {};
  const [clientSecret] = useState('mock-client-secret-for-testing'); // Mock client secret

  const options = {
    clientSecret,
    appearance: { theme: 'night' }, // Optional dark theme
  };

  return (
    <div className="payment-container">
      <h1>Checkout</h1>

      {/* Order Summary */}
      <section className="order-summary">
        <h2>Order Summary</h2>
        <div className="product-details">
          <div className="product-item">
            <img src="https://via.placeholder.com/60" alt="Event Poster" />
            <div className="product-info">
              <h3>{title}</h3>
              <p>Quantity: {quantity}</p>
            </div>
            <div className="product-price">₹{price * quantity}</div>
          </div>
        </div>
        <div className="total-section">
          <div className="total-row">
            <span>Subtotal</span>
            <span>₹{price * quantity}</span>
          </div>
          <div className="total-row">
            <span>Tax (5%)</span>
            <span>₹{(price * quantity * 0.05).toFixed(2)}</span>
          </div>
          <div className="total-row final">
            <span>Total</span>
            <span>₹{(price * quantity * 1.05).toFixed(2)}</span>
          </div>
        </div>
      </section>

      {/* Payment Form */}
      {clientSecret && (
        <Elements stripe={stripePromise} options={{ clientSecret }}>
          <CheckoutForm />
        </Elements>
      )}
    </div>
  );
};

export default PaymentPage;
