// src/pages/SeatSelectionPage.js
import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SeatSelectionPage.css';

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state?.event;

  const cheapRows = ['A', 'B'];
  const premiumRows = ['C', 'D', 'E', 'F', 'G'];
  const seatColumns = 14;

  const [selectedSeats, setSelectedSeats] = useState([]);

  const handleSeatToggle = (seatId) => {
    setSelectedSeats((prev) =>
      prev.includes(seatId)
        ? prev.filter((s) => s !== seatId)
        : [...prev, seatId]
    );
  };

  const getPrice = (row) => (cheapRows.includes(row) ? 180 : 250);

  const calculateTotal = () =>
    selectedSeats.reduce((total, seat) => {
      const row = seat.charAt(0);
      return total + getPrice(row);
    }, 0);

  const handleProceed = () => {
    if (!selectedSeats.length) {
      alert("Please select at least one seat.");
      return;
    }

    navigate('/payment', {
      state: {
        title: event?.title || 'Selected Event',
        quantity: selectedSeats.length,
        selectedSeats,
        totalPrice: calculateTotal(),
      },
    });
  };

  if (!event) {
    return <div className="error-message">Event data missing. Please go back.</div>;
  }

  return (
    <div className="seat-selection-container">
      <h2>Select Seats for {event.title}</h2>

      {[...cheapRows, ...premiumRows].map((row) => (
        <div key={row} className="seat-row">
          <span className="row-label">{row}</span>
          {[...Array(seatColumns)].map((_, colIndex) => {
            const seatId = `${row}${colIndex + 1}`;
            const selected = selectedSeats.includes(seatId);
            return (
              <div
                key={seatId}
                className={`seat ${selected ? 'selected' : ''}`}
                onClick={() => handleSeatToggle(seatId)}
              >
                {colIndex + 1}
              </div>
            );
          })}
        </div>
      ))}

      <div className="screen-label">All eyes this way please!</div>

      <div className="summary-section">
        <p>Selected Seats: {selectedSeats.join(', ') || 'None'}</p>
        <p>Total: â‚¹{calculateTotal()}</p>
        <button className="proceed-btn" onClick={handleProceed}>
          Continue to Payment ({selectedSeats.length} seat{selectedSeats.length !== 1 ? 's' : ''})
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
