import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SeatSelectionPage.css';

const seatRows = [
  { row: 'A', price: 170 },
  { row: 'B', price: 170 },
  { row: 'C', price: 170 },
  { row: 'D', price: 190 },
  { row: 'E', price: 190 },
  { row: 'F', price: 220 },
  { row: 'G', price: 220 },
];

const SeatSelectionPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const eventData = location.state;

  const [selectedSeats, setSelectedSeats] = useState([]);

  if (!eventData || !eventData.title || !eventData.price) {
    return (
      <div className="error-container">
        <p>Event data missing. Please go back.</p>
      </div>
    );
  }

  const handleSeatClick = (seat) => {
    setSelectedSeats((prev) =>
      prev.includes(seat)
        ? prev.filter((s) => s !== seat)
        : [...prev, seat]
    );
  };

  const getSeatPrice = (seatId) => {
    const row = seatId[0];
    const rowConfig = seatRows.find((r) => r.row === row);
    return rowConfig ? rowConfig.price : 0;
  };

  const totalAmount = selectedSeats.reduce(
    (total, seat) => total + getSeatPrice(seat),
    0
  );

  const handleContinue = () => {
    navigate('/payment', {
      state: {
        ...eventData,
        selectedSeats,
        quantity: selectedSeats.length,
        total: totalAmount,
      },
    });
  };

  return (
    <div className="seat-selection-container">
      <h2>Select Seats for {eventData.title}</h2>

      <div className="screen-label">Screen This Way</div>

      <div className="seat-map">
        {seatRows.map(({ row, price }) => (
          <div key={row} className="seat-row">
            <span className="row-label">{row}</span>
            {[...Array(10).keys()].map((i) => {
              const seatId = `${row}${i + 1}`;
              return (
                <div
                  key={seatId}
                  className={`seat ${selectedSeats.includes(seatId) ? 'selected' : ''}`}
                  onClick={() => handleSeatClick(seatId)}
                >
                  {seatId}
                </div>
              );
            })}
            <span className="price-label">₹{price}</span>
          </div>
        ))}
      </div>

      <div className="summary-box">
        <p>
          Selected Seats: {selectedSeats.length > 0 ? selectedSeats.join(', ') : 'None'}
        </p>
        <p>Total: ₹{totalAmount}</p>
        <button
          className="continue-button"
          disabled={selectedSeats.length === 0}
          onClick={handleContinue}
        >
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
