import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './PaymentPage.css';

const PaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { title, price, quantity, userDetails } = location.state || {};

  const [email, setEmail] = useState(userDetails?.email || '');
  const [name, setName] = useState(userDetails?.name || '');
  const [phone, setPhone] = useState(userDetails?.phone || '');
  const [paymentMethod, setPaymentMethod] = useState('card');
  const [cardNumber, setCardNumber] = useState('');
  const [expiry, setExpiry] = useState('');
  const [cvc, setCvc] = useState('');
  const [zipCode, setZipCode] = useState('');
  const [country, setCountry] = useState('India');
  const [errors, setErrors] = useState({});
  const [isProcessing, setIsProcessing] = useState(false);

  const subtotal = price * quantity;
  const tax = subtotal * 0.05;
  const total = subtotal + tax;

  useEffect(() => {
    if (!title || !price || !quantity) {
      alert("Missing event info. Please go back and select tickets again.");
      navigate(-1); // Go back
    }
  }, [title, price, quantity, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email) {
      setErrors({ email: 'Email is required' });
      return;
    }

    setIsProcessing(true);

    try {
      const res = await fetch('http://localhost:3000/api/payments/checkout', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          customerEmail: email,
          items: [{
            name: title,
            amount: price,
            currency: 'inr',
            quantity,
            description: `Booking by ${name}, Phone: ${phone}`
          }]
        }),
      });

      const data = await res.json();
      if (data.sessionId) {
        window.location.href = `https://checkout.stripe.com/c/${data.sessionId}`;
      } else {
        throw new Error(data.message || 'Checkout failed.');
      }
    } catch (err) {
      alert(`Error: ${err.message}`);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="checkout-container">
      <form onSubmit={handleSubmit} className="checkout-form">
        <h2>Confirm your booking</h2>

        <input
          type="text"
          placeholder="Full Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />

        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />

        <input
          type="tel"
          placeholder="Phone Number"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          required
        />

        <div className="summary-box">
          <p><strong>Event:</strong> {title}</p>
          <p><strong>Tickets:</strong> {quantity}</p>
          <p><strong>Subtotal:</strong> ₹{subtotal.toFixed(2)}</p>
          <p><strong>Tax:</strong> ₹{tax.toFixed(2)}</p>
          <p><strong>Total:</strong> ₹{total.toFixed(2)}</p>
        </div>

        <button type="submit" disabled={isProcessing}>
          {isProcessing ? 'Processing...' : `Pay ₹${total.toFixed(2)}`}
        </button>
      </form>
    </div>
  );
};

export default PaymentPage;
