import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SeatSelectionPage.css';

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { event } = location.state || {};

  if (!event) {
    return <div>No event data found. Please go back and select again.</div>;
  }

  const seats = Array.from({ length: 30 }, (_, i) => `S${i + 1}`);
  const [selectedSeats, setSelectedSeats] = React.useState([]);

  const toggleSeat = (seat) => {
    setSelectedSeats(prev =>
      prev.includes(seat)
        ? prev.filter(s => s !== seat)
        : [...prev, seat]
    );
  };

  const handleProceed = () => {
    if (selectedSeats.length === 0) return alert("Select at least one seat");

    navigate('/payment', {
      state: {
        title: event.title,
        price: event.price,
        quantity: selectedSeats.length,
        selectedSeats
      }
    });
  };

  return (
    <div className="seat-selection-container">
      <h2>Select your seats for {event.title}</h2>
      <div className="seat-grid">
        {seats.map(seat => (
          <div
            key={seat}
            className={`seat ${selectedSeats.includes(seat) ? 'selected' : ''}`}
            onClick={() => toggleSeat(seat)}
          >
            {seat}
          </div>
        ))}
      </div>
      <div className="seat-actions">
        <p>Selected Seats: {selectedSeats.join(', ')}</p>
        <button onClick={handleProceed}>Proceed to Payment</button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
