// src/pages/ConcertSeatSelectionPage.js
import React, { useState } from 'react';
import './ConcertSeatSelectionPage.css';
import { useNavigate, useLocation } from 'react-router-dom';

const ConcertSeatSelectionPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { event } = location.state || {};
  const [selectedSeats, setSelectedSeats] = useState([]);

  const tiers = [
    { name: 'Platinum', rows: ['A', 'B'], price: 500 },
    { name: 'Gold', rows: ['C', 'D'], price: 350 },
    { name: 'Silver', rows: ['E', 'F'], price: 200 },
  ];

  const seatsPerRow = 14;

  const toggleSeat = (seatId) => {
    setSelectedSeats((prev) =>
      prev.includes(seatId)
        ? prev.filter((s) => s !== seatId)
        : [...prev, seatId]
    );
  };

  const handlePayment = () => {
    if (selectedSeats.length === 0) {
      alert('Please select at least one seat');
      return;
    }

    const totalPrice = selectedSeats.reduce((sum, seatId) => {
      const row = seatId.split('-')[1][0]; // Extract row letter
      const tier = tiers.find(t => t.rows.includes(row));
      return sum + (tier?.price || 0);
    }, 0);

    navigate('/payment', {
      state: {
        title: event?.title || "Concert",
        quantity: selectedSeats.length,
        selectedSeats,
        price: totalPrice / selectedSeats.length, // average price
      },
    });
  };

  return (
    <div className="concert-seat-selection">
      <h2>{event?.title || 'Select Seats for Concert'}</h2>
      <div className="stage">STAGE</div>

      {tiers.map((tier, tierIndex) => (
        <div key={tier.name} className="tier-section">
          <h3 className="tier-label">{tier.name} (₹{tier.price})</h3>
          {tier.rows.map((row) => (
            <div key={row} className="seat-row">
              <span className="row-label">{row}</span>
              {[...Array(seatsPerRow)].map((_, seatIndex) => {
                const seatId = `${tier.name}-${row}${seatIndex + 1}`;
                return (
                  <div
                    key={seatId}
                    className={`seat ${selectedSeats.includes(seatId) ? 'selected' : ''}`}
                    onClick={() => toggleSeat(seatId)}
                  >
                    {seatIndex + 1}
                  </div>
                );
              })}
            </div>
          ))}
        </div>
      ))}

      <div className="summary">
        <p>Selected Seats: {selectedSeats.join(', ') || 'None'}</p>
        <p>Total Price: ₹{selectedSeats.reduce((sum, seatId) => {
          const row = seatId.split('-')[1][0];
          const tier = tiers.find(t => t.rows.includes(row));
          return sum + (tier?.price || 0);
        }, 0)}</p>
        <button onClick={handlePayment} className="pay-btn">
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default ConcertSeatSelectionPage;
