import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import EventCard from '../components/EventCard';
import './EventsPage.css';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [filters, setFilters] = useState({
    date: '',
    location: 'Mumbai',
    category: 'all',
    priceRange: [0, 5000],
    language: 'all',
  });

  const transformEvent = (backendEvent) => {
    let type = 'other';
    if (backendEvent.type === 'MovieSchema') {
      type = 'movie';
    } else if (backendEvent.type === 'ConcertSchema') {
      type = 'concert';
    } else if (backendEvent.type === 'TheatreSchema' || backendEvent.type === 'Theatre') {
      type = 'theatre';
    }
    return {
      id: backendEvent.eventId,
      title: backendEvent.name,
      date: backendEvent.eventDate,
      poster: backendEvent.posterImage || "https://via.placeholder.com/150",
      type: type,
      price: 0,
      location: "Mumbai",
      language: backendEvent.language || 'English', // default
    };
  };

  useEffect(() => {
    fetch('http://localhost:3000/api/events')
      .then((res) => res.json())
      .then((data) => {
        if (data.events) {
          const transformed = data.events.map(transformEvent);
          setEvents(transformed);
        }
      })
      .catch((err) => console.error('Error fetching events:', err));
  }, []);

  const filteredEvents = events.filter(event => {
    const selectedDate = filters.date ? new Date(filters.date) : null;
    const eventDate = new Date(event.date);
    const matchesDate =
      !selectedDate ||
      (eventDate.toDateString() === selectedDate.toDateString());

    const matchesLocation = event.location === filters.location;
    const matchesCategory = filters.category === 'all' || event.type === filters.category;
    const matchesLanguage = filters.language === 'all' || event.language === filters.language;
    const matchesPrice = event.price >= filters.priceRange[0] && event.price <= filters.priceRange[1];

    return matchesDate && matchesLocation && matchesCategory && matchesLanguage && matchesPrice;
  });

  return (
    <div className="events-page">
      <Header />

      <div className="filters-container">
        <div className="filter-group">
          <label>Date</label>
          <input
            type="date"
            value={filters.date}
            onChange={(e) => setFilters({ ...filters, date: e.target.value })}
          />
        </div>

        <div className="filter-group">
          <label>Location</label>
          <select
            value={filters.location}
            onChange={(e) => setFilters({ ...filters, location: e.target.value })}
          >
            <option value="Mumbai">Mumbai</option>
            <option value="Delhi">Delhi</option>
            <option value="Bengaluru">Bengaluru</option>
          </select>
        </div>

        <div className="filter-group">
          <label>Category</label>
          <select
            value={filters.category}
            onChange={(e) => setFilters({ ...filters, category: e.target.value })}
          >
            <option value="all">All Events</option>
            <option value="concert">Concerts</option>
            <option value="movie">Movies</option>
            <option value="theatre">Theatre</option>
          </select>
        </div>

        <div className="filter-group">
          <label>Language</label>
          <select
            value={filters.language}
            onChange={(e) => setFilters({ ...filters, language: e.target.value })}
          >
            <option value="all">All</option>
            <option value="English">English</option>
            <option value="Hindi">Hindi</option>
            <option value="Marathi">Marathi</option>
          </select>
        </div>

        <div className="filter-group">
          <label>Price Range</label>
          <input
            type="range"
            min="0"
            max="5000"
            value={filters.priceRange[1]}
            onChange={(e) => setFilters({ ...filters, priceRange: [0, Number(e.target.value)] })}
          />
          <div className="price-range">₹0 - ₹{filters.priceRange[1]}</div>
        </div>
      </div>

      <div className="events-grid">
        {filteredEvents.length === 0 ? (
          <p style={{ padding: '1rem' }}>No events match your filters.</p>
        ) : (
          filteredEvents.map(event => (
            <EventCard key={event.id} event={event} />
          ))
        )}
      </div>
    </div>
  );
};

export default EventsPage;
