import React, { useState } from 'react';
import './SeatSelectionPage.css';
import { useLocation, useNavigate } from 'react-router-dom';

const ROWS = ['H', 'G', 'F', 'E', 'D', 'C', 'B', 'A'];
const SEAT_COLUMNS = 25;

const getPrice = (row) => ['A', 'B'].includes(row) ? 150 : 250;

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state?.event;

  const [selectedSeats, setSelectedSeats] = useState([]);

  if (!event) {
    return <p className="error-message">Event data missing. Please go back.</p>;
  }

  const toggleSeat = (seat) => {
    setSelectedSeats((prev) =>
      prev.includes(seat)
        ? prev.filter((s) => s !== seat)
        : [...prev, seat]
    );
  };

  const handleProceed = () => {
    if (selectedSeats.length === 0) {
      alert('Please select at least one seat.');
      return;
    }

    const total = selectedSeats.reduce((sum, seat) => {
      const row = seat[0];
      return sum + getPrice(row);
    }, 0);

    navigate('/payment', {
      state: {
        title: event.title,
        selectedSeats,
        quantity: selectedSeats.length,
        price: total,
      }
    });
  };

  return (
    <div className="seat-selection-container">
      <h2>Select Seats for {event.title}</h2>

      <div className="seat-map">
        {ROWS.map((row, rowIndex) => (
          <div
            key={row}
            className={`seat-row ${row === 'C' ? 'with-gap-top' : ''} ${row === 'B' ? 'gap-after' : ''}`}
          >
            {Array.from({ length: SEAT_COLUMNS }).map((_, index) => {
              const seatNumber = `${row}${index + 1}`;
              const price = getPrice(row);
              return (
                <div
                  key={seatNumber}
                  className={`seat ${selectedSeats.includes(seatNumber) ? 'selected' : ''} ${price === 150 ? 'low-tier' : 'high-tier'}`}
                  onClick={() => toggleSeat(seatNumber)}
                >
                  {seatNumber}
                </div>
              );
            })}
          </div>
        ))}
      </div>

      <div className="screen-label">
        <div className="screen-glass">SCREEN</div>
      </div>

      <div className="booking-summary">
        <p>Selected Seats: {selectedSeats.length ? selectedSeats.join(', ') : 'None'}</p>
        <p>Total: â‚¹{selectedSeats.reduce((sum, seat) => sum + getPrice(seat[0]), 0)}</p>
        <button onClick={handleProceed} className="pay-button">
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
