import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './EventsPage.css';

const EventsPage = () => {
  // Mock data
  const mockEvents = [
    {
      id: "1",
      name: "Summer Music Festival",
      date: "2025-06-15",
      location: "Central Park, New York",
      image: "https://example.com/festival.jpg",
      description: "Join us for an unforgettable night of live music..."
    },
    {
      id: "2",
      name: "Winter Wonderland Concert",
      date: "2025-12-20",
      location: "Madison Square Garden, New York",
      image: "https://example.com/concert.jpg",
      description: "Experience the magic of winter with live music."
    }
  ];

  const [events] = useState(mockEvents);

  return (
    <div className="events-container">
      <h1>Upcoming Events</h1>
      <div className="events-grid">
        {events.map(event => (
          <Link 
            to={`/events/${event.id}`} 
            key={event.id} 
            className="event-card"
          >
            <img 
              src={event.image} 
              alt={event.name} 
              className="event-image"
            />
            <div className="event-info">
              <h3>{event.name}</h3>
              <p>{new Date(event.date).toLocaleDateString()}</p>
              <p>{event.location}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default EventsPage;
