import { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';

const PaymentSuccessPage = () => {
  const [searchParams] = useSearchParams();
  const sessionId = searchParams.get('session_id');
  const [session, setSession] = useState(null);

  useEffect(() => {
    const fetchSession = async () => {
      const res = await fetch(`http://localhost:3000/api/payment/session/${sessionId}`);
      const data = await res.json();
      setSession(data);
    };
    if (sessionId) fetchSession();
  }, [sessionId]);

  if (!session) return <p>Loading...</p>;

  return (
    <div style={{ padding: '40px', textAlign: 'center' }}>
      <h2>âœ… Payment Successful!</h2>
      <p>Thank you for booking {session.metadata.eventTitle}.</p>
      <p>Confirmation sent to: {session.customer_email}</p>
      <p>Seats: {session.metadata.selectedSeats}</p>
      <p>Total Paid: â‚¹{(session.amount_total / 100).toFixed(0)}</p>
      <p>Enjoy your event! ðŸŽ‰</p>
    </div>
  );
};
export default PaymentSuccessPage;
