import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './SeatSelectionPage.css';

const SeatSelectionPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const event = location.state?.event;

  const rows = ['H', 'G', 'F', 'E', 'D', 'C', 'B', 'A']; // A, B are near the screen
  const seatsPerSection = {
    left: 8,
    middle: 25,
    right: 8,
  };

  const getSeatPrice = (row) => ['A', 'B'].includes(row) ? 150 : 250;

  const [selectedSeats, setSelectedSeats] = useState([]);

  const toggleSeat = (seatId, price) => {
    setSelectedSeats(prev => {
      const exists = prev.find(s => s.id === seatId);
      return exists
        ? prev.filter(s => s.id !== seatId)
        : [...prev, { id: seatId, price }];
    });
  };

  const handleProceed = () => {
    if (!event || selectedSeats.length === 0) {
      alert('Please select your seats');
      return;
    }

    navigate('/payment', {
      state: {
        title: event.title,
        price: selectedSeats.reduce((sum, s) => sum + s.price, 0),
        quantity: selectedSeats.length,
        selectedSeats,
        userDetails: {},
      }
    });
  };

  if (!event) return <p className="error">Event data missing. Please go back.</p>;

  return (
    <div className="seat-page-container">
      <h2>Select Seats for {event.title}</h2>
      <div className="seat-layout">
        {rows.map((row, rowIndex) => (
          <div
            className={`seat-row ${row === 'C' ? 'gap-row' : ''}`}
            key={rowIndex}
          >
            {['left', 'middle', 'right'].map((section, secIndex) => (
              <div className="seat-section" key={secIndex}>
                {Array.from({ length: seatsPerSection[section] }, (_, seatIndex) => {
                  const seatId = `${row}-${section[0].toUpperCase()}${seatIndex + 1}`;
                  const price = getSeatPrice(row);
                  const isSelected = selectedSeats.find(s => s.id === seatId);
                  return (
                    <div
                      key={seatId}
                      className={`seat ${isSelected ? 'selected' : ''} ${price === 150 ? 'cheap' : 'regular'}`}
                      onClick={() => toggleSeat(seatId, price)}
                    >
                      {seatId}
                    </div>
                  );
                })}
              </div>
            ))}
          </div>
        ))}
      </div>

      {/* Screen */}
      <div className="screen">SCREEN</div>

      <div className="seat-summary">
        <p>Selected Seats: {selectedSeats.map(s => s.id).join(', ') || 'None'}</p>
        <p>Total: â‚¹{selectedSeats.reduce((sum, s) => sum + s.price, 0)}</p>
        <button onClick={handleProceed}>
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
