// src/pages/SeatSelectionPage.js
import React, { useState } from 'react';
import './SeatSelectionPage.css';
import { useLocation, useNavigate } from 'react-router-dom';

const ROWS = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H'];
const SEATS_PER_ROW = 14;

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state?.event;

  const [selectedSeats, setSelectedSeats] = useState([]);

  if (!event) {
    return <div className="error">Event data not available. Please go back and try again.</div>;
  }

  const getPrice = (row) => {
    if (['A', 'B'].includes(row)) return 150; // Lower rows near screen
    return 250; // Remaining rows
  };

  const handleSeatClick = (seatId) => {
    setSelectedSeats((prev) =>
      prev.includes(seatId) ? prev.filter(s => s !== seatId) : [...prev, seatId]
    );
  };

  const totalPrice = selectedSeats.reduce((sum, seatId) => {
    const row = seatId.charAt(0);
    return sum + getPrice(row);
  }, 0);

  const handleProceed = () => {
    navigate('/payment', {
      state: {
        title: event.title,
        selectedSeats,
        quantity: selectedSeats.length,
        price: totalPrice,
      },
    });
  };

  return (
    <div className="seat-selection-container">
      <h2>Select Seats for {event.title}</h2>
      <div className="seat-map">
        {ROWS.map((row) => (
          <div key={row} className="seat-row">
            {[...Array(SEATS_PER_ROW)].map((_, i) => {
              const seatId = `${row}${i + 1}`;
              const isSelected = selectedSeats.includes(seatId);

              const isPath = (i === 4 || i === 10); // Aisle between seats
              return isPath ? (
                <div key={`${seatId}-gap`} className="seat-gap" />
              ) : (
                <div
                  key={seatId}
                  className={`seat ${isSelected ? 'selected' : ''}`}
                  onClick={() => handleSeatClick(seatId)}
                >
                  {seatId}
                </div>
              );
            })}
          </div>
        ))}
      </div>

      <div className="screen">SCREEN THIS WAY</div>

      <div className="summary">
        <p>Selected Seats: {selectedSeats.join(', ') || 'None'}</p>
        <p>Total Price: â‚¹{totalPrice}</p>
        <button
          disabled={selectedSeats.length === 0}
          onClick={handleProceed}
        >
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
