// src/components/EventFilter.js
import React, { useState } from 'react';
import './EventFilter.css';

const EventFilter = ({ onFilter }) => {
  const [localFilters, setLocalFilters] = useState({
    date: '',
    start_time: '',
    location: '',
    genre: '',
    language: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLocalFilters({
      ...localFilters,
      [name]: value,
    });
  };

  const handleApplyFilters = () => {
    onFilter(localFilters);
  };

  return (
    <div className="event-filter">
      <div className="filter-field">
        <label>Date</label>
        <input type="date" name="date" value={localFilters.date} onChange={handleChange} />
      </div>
      <div className="filter-field">
        <label>Start Time</label>
        <input type="time" name="start_time" value={localFilters.start_time} onChange={handleChange} />
      </div>
      <div className="filter-field">
        <label>Location</label>
        <input type="text" name="location" value={localFilters.location} onChange={handleChange} placeholder="e.g., Mumbai" />
      </div>
      <div className="filter-field">
        <label>Genre</label>
        <input type="text" name="genre" value={localFilters.genre} onChange={handleChange} />
      </div>
      <div className="filter-field">
        <label>Language</label>
        <input type="text" name="language" value={localFilters.language} onChange={handleChange} />
      </div>
      <button onClick={handleApplyFilters}>Apply Filters</button>
    </div>
  );
};

export default EventFilter;
