// src/pages/SeatSelectionPage.js
import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SeatSelectionPage.css';

const tiers = [
  { name: "Normal", price: 170, rows: ['E', 'F'] },
  { name: "Executive", price: 190, rows: ['G', 'H', 'J', 'K', 'L', 'M'] }
];

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state;

  const [selectedSeats, setSelectedSeats] = useState([]);

  if (!event) {
    return <p className="error">Event data missing. Please go back.</p>;
  }

  const toggleSeat = (seat) => {
    setSelectedSeats((prev) =>
      prev.includes(seat)
        ? prev.filter((s) => s !== seat)
        : [...prev, seat]
    );
  };

  const handlePayment = () => {
    if (selectedSeats.length === 0) {
      alert('Please select at least one seat');
      return;
    }
    navigate('/payment', {
      state: {
        ...event,
        quantity: selectedSeats.length,
        selectedSeats
      }
    });
  };

  return (
    <div className="seat-page">
      <h2>Select Seats for {event.title}</h2>

      <div className="seat-layout">
        {tiers.map((tier) => (
          <div key={tier.name}>
            <h4 className="tier-label">Rs. {tier.price} {tier.name.toUpperCase()}</h4>
            {tier.rows.map((row) => (
              <div key={row} className="seat-row">
                <span className="row-label">{row}</span>
                {[...Array(20)].map((_, i) => {
                  const seatId = `${row}-${i + 1}`;
                  const isSelected = selectedSeats.includes(seatId);
                  return (
                    <div
                      key={seatId}
                      className={`seat ${isSelected ? 'selected' : ''}`}
                      onClick={() => toggleSeat(seatId)}
                    >
                      {i + 1}
                    </div>
                  );
                })}
              </div>
            ))}
          </div>
        ))}
      </div>

      <div className="screen">All eyes this way please!</div>

      <div className="selection-summary">
        <div>
          <strong>Selected Seats:</strong> {selectedSeats.join(', ') || 'None'}
        </div>
        <div>
          <strong>Total:</strong> â‚¹{selectedSeats.length * 190}
        </div>
        <button className="continue-btn" onClick={handlePayment}>
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
