import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SeatSelectionPage.css';

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state?.event;

  const [selectedSeats, setSelectedSeats] = useState([]);

  const rows = [
    { label: 'A', price: 200 },
    { label: 'B', price: 200 },
    { label: 'C', price: 350 },
    { label: 'D', price: 350 },
    { label: 'E', price: 350 },
    { label: 'F', price: 350 },
    { label: 'G', price: 350 }
  ];

  const seatsPerRow = 14;

  const toggleSeat = (seatId) => {
    setSelectedSeats(prev =>
      prev.includes(seatId)
        ? prev.filter(seat => seat !== seatId)
        : [...prev, seatId]
    );
  };

  const getPrice = (seatId) => {
    const rowLabel = seatId.charAt(0);
    const row = rows.find(r => r.label === rowLabel);
    return row ? row.price : 0;
  };

  const totalAmount = selectedSeats.reduce((acc, seat) => acc + getPrice(seat), 0);

  const handlePayment = () => {
    if (!event || selectedSeats.length === 0) {
      alert("Please select your seats before proceeding.");
      return;
    }

    navigate('/payment', {
      state: {
        title: event.title,
        price: totalAmount / selectedSeats.length,
        quantity: selectedSeats.length,
        selectedSeats
      }
    });
  };

  if (!event) {
    return <div className="seat-error">Event data missing. Please go back.</div>;
  }

  return (
    <div className="seat-selection-container">
      <h2 className="seat-heading">Select Seats for {event.title}</h2>

      <div className="seat-map-wrapper">
        {rows.map(row => (
          <div key={row.label} className="seat-row">
            <div className="row-label">{row.label}</div>
            {[...Array(seatsPerRow)].map((_, index) => {
              const seatId = `${row.label}${index + 1}`;
              return (
                <div
                  key={seatId}
                  className={`seat ${selectedSeats.includes(seatId) ? 'selected' : ''}`}
                  onClick={() => toggleSeat(seatId)}
                >
                  {index + 1}
                </div>
              );
            })}
            <div className="row-price">₹{row.price}</div>
          </div>
        ))}
      </div>

      <div className="screen">All eyes this way please!</div>

      <div className="selection-summary">
        <p><strong>Selected Seats:</strong> {selectedSeats.length > 0 ? selectedSeats.join(', ') : 'None'}</p>
        <p><strong>Total:</strong> ₹{totalAmount}</p>
        <button
          className="payment-btn"
          onClick={handlePayment}
          disabled={selectedSeats.length === 0}
        >
          Continue to Payment ({selectedSeats.length} seat{selectedSeats.length !== 1 ? 's' : ''})
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
