// src/pages/EventsPage.js
import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import EventCard from '../components/EventCard';
import './EventsPage.css';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');

  useEffect(() => {
    // Mock API call
    const mockEvents = [
      {
        id: 1,
        title: "Coldplay: Music of the Spheres World Tour",
        type: "concert",
        date: "2025-06-15",
        price: 5000,
        venue: "DY Patil Stadium, Mumbai",
        poster: "https://source.unsplash.com/random/800x600?concert"
      },
      {
        id: 2,
        title: "Avengers: Endgame",
        type: "movie",
        date: "2025-05-01",
        price: 300,
        venue: "PVR Cinemas, Mumbai",
        poster: "https://source.unsplash.com/random/800x600?movie"
      },
      {
        id: 3,
        title: "Sunburn Arena - Martin Garrix",
        type: "concert",
        date: "2025-07-20",
        price: 3500,
        venue: "Nesco Grounds, Mumbai",
        poster: "https://source.unsplash.com/random/800x600?edm"
      }
    ];
    setEvents(mockEvents);
  }, []);

  return (
    <div className="events-page">
      <Header />

      <div className="filters-container">
        <div className="filters">
          <button 
            className={`filter-btn ${selectedCategory === 'all' ? 'active' : ''}`}
            onClick={() => setSelectedCategory('all')}
          >
            All Events
          </button>
          <button 
            className={`filter-btn ${selectedCategory === 'concert' ? 'active' : ''}`}
            onClick={() => setSelectedCategory('concert')}
          >
            Concerts
          </button>
          <button 
            className={`filter-btn ${selectedCategory === 'movie' ? 'active' : ''}`}
            onClick={() => setSelectedCategory('movie')}
          >
            Movies
          </button>
        </div>
      </div>

      <div className="events-grid">
        {events
          .filter(event => selectedCategory === 'all' || event.type === selectedCategory)
          .map(event => (
            <EventCard key={event.id} event={event} />
          ))}
      </div>
    </div>
  );
};

export default EventsPage;
