import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import BookingModal from '../components/BookingModal';
import './EventDetailsPage.css';

const EventDetailsPage = () => {
  const { eventId } = useParams();
  const [event, setEvent] = useState(null);
  const [showBookingModal, setShowBookingModal] = useState(false);

  // Mock API data (replace with actual API call later)
  useEffect(() => {
    const mockEvents = {
      '1': {
        id: "1",
        type: "movie",
        title: "Avengers: Endgame",
        date: "2025-05-01",
        time: "7:30 PM",
        venue: "PVR Cinemas",
        price: 300,
        poster: "https://example.com/movie-poster.jpg",
        details: {
          cast: ["Robert Downey Jr.", "Chris Evans", "Scarlett Johansson"],
          director: "Anthony and Joe Russo",
          duration: "3h 1m"
        }
      },
      '2': {
        id: "2",
        type: "concert",
        title: "Coldplay World Tour",
        date: "2025-06-15",
        time: "8:00 PM",
        venue: "Mumbai Arena",
        price: 5000,
        poster: "https://example.com/concert-poster.jpg",
        details: {
          artists: ["Coldplay"],
          sponsors: ["Coca-Cola", "Visa"],
          duration: "2h 30m"
        }
      }
    };

    setEvent(mockEvents[eventId]);
  }, [eventId]);

  const renderEventSpecificDetails = () => {
    if(!event) return null;

    switch(event.type) {
      case 'movie':
        return (
          <div className="details-section">
            <h3>Cast</h3>
            <ul>{event.details.cast.map((actor,i) => 
              <li key={i}>{actor}</li>)}</ul>
            <p>Director: {event.details.director}</p>
          </div>
        );

      case 'concert':
        return (
          <div className="details-section">
            <h3>Artists</h3>
            <ul>{event.details.artists.map((artist,i) => 
              <li key={i}>{artist}</li>)}</ul>
            <h3>Sponsors</h3>
            <p>{event.details.sponsors.join(', ')}</p>
          </div>
        );

      default:
        return (
          <div className="details-section">
            <h3>Organizers</h3>
            <p>{event.details.organizers?.join(', ') || 'TBA'}</p>
          </div>
        );
    }
  };

  return (
    <div className="event-details-page">
      {event && (
        <>
          <div className="event-header">
            <img src={event.poster} alt={event.title} className="poster" />
            <div className="header-info">
              <h1>{event.title}</h1>
              <div className="meta-info">
                <span>{new Date(event.date).toLocaleDateString()}</span>
                <span>{event.time}</span>
                <span>{event.venue}</span>
              </div>
              <button 
                className="book-button"
                onClick={() => setShowBookingModal(true)}
              >
                Book Now
              </button>
            </div>
          </div>

          <div className="event-content">
            {renderEventSpecificDetails()}

            <div className="additional-info">
              <h3>Event Type</h3>
              <p>{event.type.charAt(0).toUpperCase() + event.type.slice(1)}</p>

              <h3>Price</h3>
              <p>â‚¹ {event.price}</p>
            </div>
          </div>
        </>
      )}

      {showBookingModal && (
        <BookingModal 
          event={event} 
          onClose={() => setShowBookingModal(false)}
        />
      )}
    </div>
  );
};

export default EventDetailsPage;
