import React, { useState } from 'react';
import './BookingModal.css';
import { useNavigate } from 'react-router-dom';

const BookingModal = ({ event, onClose }) => {
  const navigate = useNavigate();
  const [selectedSeats, setSelectedSeats] = useState([]);

  // Generate seats for example (can be replaced by backend-driven seat map later)
  const seats = Array.from({ length: 36 }, (_, i) => {
    const row = String.fromCharCode(65 + Math.floor(i / 6)); // A-F rows
    const num = (i % 6) + 1;
    return `${row}${num}`;
  });

  const toggleSeat = (seat) => {
    setSelectedSeats((prev) =>
      prev.includes(seat)
        ? prev.filter((s) => s !== seat)
        : [...prev, seat]
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (selectedSeats.length === 0) {
      alert('Please select at least one seat.');
      return;
    }

    navigate('/payment', {
      state: {
        title: event.title,
        price: event.price,
        quantity: selectedSeats.length,
        selectedSeats,
        eventId: event.id,
      },
    });
  };

  return (
    <div className="modal-overlay">
      <div className="booking-modal">
        <div className="modal-header">
          <h2>Select Your Seats for {event.title}</h2>
          <button className="close-btn" onClick={onClose}>
            ×
          </button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="seat-map">
            {seats.map((seat) => (
              <div
                key={seat}
                className={`seat ${selectedSeats.includes(seat) ? 'selected' : ''}`}
                onClick={() => toggleSeat(seat)}
              >
                {seat}
              </div>
            ))}
          </div>

          <div className="price-summary">
            <p>Total Seats: {selectedSeats.length}</p>
            <p>Total Price: ₹{selectedSeats.length * event.price}</p>
          </div>

          <button type="submit" className="confirm-btn">
            Proceed to Payment
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingModal;
