// src/pages/SeatSelectionPage.js
import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SeatSelectionPage.css';

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state?.event;

  const [selectedSeats, setSelectedSeats] = useState([]);

  if (!event) {
    return (
      <div className="seat-selection-page">
        <h2>Event data missing. Please go back.</h2>
      </div>
    );
  }

  const silverPrice = 150;
  const goldPrice = 250;

  const seatMap = {
    Silver: ['A', 'B'],
    Gold: ['C', 'D', 'E', 'F', 'G'],
  };

  const seatsPerRow = 12;

  const toggleSeat = (seat) => {
    setSelectedSeats((prev) =>
      prev.includes(seat) ? prev.filter((s) => s !== seat) : [...prev, seat]
    );
  };

  const getSeatPrice = (seat) => {
    const row = seat.charAt(0);
    if (seatMap.Silver.includes(row)) return silverPrice;
    return goldPrice;
  };

  const totalAmount = selectedSeats.reduce((acc, seat) => acc + getSeatPrice(seat), 0);

  const handlePayment = () => {
    if (selectedSeats.length === 0) {
      alert('Please select at least one seat.');
      return;
    }

    navigate('/payment', {
      state: {
        title: event.title,
        quantity: selectedSeats.length,
        price: totalAmount,
        selectedSeats,
      },
    });
  };

  return (
    <div className="seat-selection-page">
      <h2>Select Seats for {event.title}</h2>

      {Object.entries(seatMap).map(([tier, rows]) => (
        <div key={tier} className="tier-section">
          <h3>{tier} (₹{tier === 'Silver' ? silverPrice : goldPrice})</h3>
          {rows.map((row) => (
            <div key={row} className="seat-row">
              {Array.from({ length: seatsPerRow }, (_, i) => {
                const seatId = `${row}${i + 1}`;
                return (
                  <div
                    key={seatId}
                    className={`seat ${selectedSeats.includes(seatId) ? 'selected' : ''}`}
                    onClick={() => toggleSeat(seatId)}
                  >
                    {seatId}
                  </div>
                );
              })}
            </div>
          ))}
        </div>
      ))}

      <div className="screen-label">SCREEN THIS WAY</div>

      <div className="summary">
        <p>Selected Seats: {selectedSeats.join(', ') || 'None'}</p>
        <p>Total: ₹{totalAmount}</p>
        <button onClick={handlePayment} className="proceed-btn">
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
