import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './EventsPage.css';

const EventsPage = () => {
  const [events, setEvents] = useState([]);

  // Mock data for events
  useEffect(() => {
    const mockEvents = [
      {
        id: "1",
        name: "Summer Music Festival",
        date: "2025-06-15",
        location: "Central Park, New York",
        image: "https://images.unsplash.com/photo-1529156069898-49953e39b3ac", // Replace with your image URL
        description: "Join us for an unforgettable night of live music."
      },
      {
        id: "2",
        name: "Winter Wonderland Concert",
        date: "2025-12-20",
        location: "Madison Square Garden, New York",
        image: "https://images.unsplash.com/photo-1515169067865-dae1abe0b6b3", // Replace with your image URL
        description: "Experience the magic of winter with live music."
      }
    ];
    setEvents(mockEvents);
  }, []);

  return (
    <div className="events-page">
      {/* Background Section */}
      <div className="background-image">
        <h1>Discover Amazing Events Near You</h1>
      </div>

      {/* Upcoming Events Section */}
      <div className="events-container">
        <h2>Upcoming Events</h2>
        <div className="events-grid">
          {events.map(event => (
            <Link to={`/events/${event.id}`} key={event.id} className="event-card">
              <img src={event.image} alt={event.name} className="event-image" />
              <div className="event-info">
                <h3>{event.name}</h3>
                <p>{new Date(event.date).toLocaleDateString()}</p>
                <p>{event.location}</p>
              </div>
            </Link>
          ))}
        </div>
      </div>

      {/* Additional Section (e.g., Popular Categories) */}
      <div className="categories-section">
        <h2>Popular Categories</h2>
        <div className="categories-grid">
          <div className="category-card">Music</div>
          <div className="category-card">Sports</div>
          <div className="category-card">Theatre</div>
          <div className="category-card">Comedy</div>
        </div>
      </div>
    </div>
  );
};

export default EventsPage;
