import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import EventCard from '../components/EventCard';
import './EventsPage.css';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [filters, setFilters] = useState({
    date: '',
    location: 'Mumbai',
    category: 'all',
    priceRange: [0, 5000],
  });
  const [showFilters, setShowFilters] = useState(false);

  const transformEvent = (backendEvent, type = 'other') => ({
    id: backendEvent.movieUID || backendEvent.concertUUID || backendEvent.theatreUUID || backendEvent.otherEventUUID || backendEvent.eventId,
    title: backendEvent.name,
    date: backendEvent.screeningDate || backendEvent.date,
    poster: backendEvent.posterImage || 'https://via.placeholder.com/150',
    type,
    price: 0,
    location: 'Mumbai'
  });

  const fetchFilteredEvents = async () => {
    try {
      const params = new URLSearchParams();

      if (filters.date) params.append('date', filters.date);
      if (filters.location) params.append('location', filters.location);
      if (filters.category !== 'all') params.append('genre', filters.category);

      const res = await fetch(`http://localhost:3000/api/filter/filter?${params.toString()}`);
      const data = await res.json();

      const combined = [
        ...data.movies.map(e => transformEvent(e, 'movie')),
        ...data.concerts.map(e => transformEvent(e, 'concert')),
        ...data.theatres.map(e => transformEvent(e, 'theatre')),
        ...data.otherEvents.map(e => transformEvent(e, 'other')),
      ];

      setEvents(combined);
    } catch (err) {
      console.error('Error fetching filtered events:', err);
    }
  };

  useEffect(() => {
    fetchFilteredEvents();
  }, [filters]);

  return (
    <div className="events-page">
      <Header />

      <div className="filter-toggle">
        <button onClick={() => setShowFilters(!showFilters)}>
          {showFilters ? 'Hide Filters' : 'Show Filters'}
        </button>
      </div>

      {showFilters && (
        <div className="filters-container">
          <div className="filter-group">
            <label>Date</label>
            <input
              type="date"
              value={filters.date}
              onChange={(e) => setFilters({ ...filters, date: e.target.value })}
            />
          </div>

          <div className="filter-group">
            <label>Location</label>
            <select
              value={filters.location}
              onChange={(e) => setFilters({ ...filters, location: e.target.value })}
            >
              <option value="Mumbai">Mumbai</option>
              <option value="Delhi">Delhi</option>
              <option value="Bengaluru">Bengaluru</option>
            </select>
          </div>

          <div className="filter-group">
            <label>Category</label>
            <select
              value={filters.category}
              onChange={(e) => setFilters({ ...filters, category: e.target.value })}
            >
              <option value="all">All</option>
              <option value="movie">Movies</option>
              <option value="concert">Concerts</option>
              <option value="theatre">Theatre</option>
            </select>
          </div>

          <div className="filter-group">
            <label>Price Range</label>
            <input
              type="range"
              min="0"
              max="5000"
              value={filters.priceRange[1]}
              onChange={(e) =>
                setFilters({ ...filters, priceRange: [0, Number(e.target.value)] })
              }
            />
            <div className="price-range">₹0 - ₹{filters.priceRange[1]}</div>
          </div>
        </div>
      )}

      <div className="events-grid">
        {events.length === 0 ? (
          <div style={{ padding: '20px', textAlign: 'center' }}>No events found.</div>
        ) : (
          events.map((event) => <EventCard key={event.id} event={event} />)
        )}
      </div>
    </div>
  );
};

export default EventsPage;
