// src/pages/EventsPage.js
import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import EventCard from '../components/EventCard';
import EventFilter from '../components/EventFilter';
import './EventsPage.css';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [filters, setFilters] = useState(null);

  // Function to transform backend event data into frontend structure
  const transformEvent = (backendEvent) => {
    let type = 'other';
    if (backendEvent.type === 'MovieSchema') {
      type = 'movie';
    } else if (backendEvent.type === 'ConcertSchema') {
      type = 'concert';
    } else if (backendEvent.type === 'TheatreSchema' || backendEvent.type === 'Theatre') {
      type = 'theatre';
    }
    return {
      id: backendEvent.eventId,
      title: backendEvent.name,
      date: backendEvent.eventDate,
      poster: backendEvent.posterImage || "https://via.placeholder.com/150",
      type: type,
      // Default values (adjust as needed)
      price: 0,
      location: "Mumbai",
    };
  };

  // Fetch events from the backend filter endpoint whenever filters change.
  useEffect(() => {
    let url = 'http://localhost:3000/api/filter/filter';
    if (filters) {
      const params = new URLSearchParams(filters);
      url += `?${params.toString()}`;
    }
    console.log('Fetching events with URL:', url);
    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        // The filter endpoint returns separate arrays; combine them:
        const combined = [
          ...data.movies,
          ...data.concerts,
          ...data.theatres,
          ...data.otherEvents,
        ];
        const transformedEvents = combined.map(transformEvent);
        console.log('Transformed events:', transformedEvents);
        setEvents(transformedEvents);
      })
      .catch((err) => console.error('Error fetching filtered events:', err));
  }, [filters]);

  return (
    <div className="events-page">
      <Header />
      <EventFilter onFilter={setFilters} />
      <div className="events-grid">
        {events.map((event) => (
          <EventCard key={event.id} event={event} />
        ))}
      </div>
    </div>
  );
};

export default EventsPage;
