import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './PaymentPage.css';

const PaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const eventData = location.state;

  useEffect(() => {
    if (!eventData || !eventData.title || !eventData.price) {
      navigate('/events', { replace: true });
    }
  }, [eventData, navigate]);

  if (!eventData) {
    return <p className="error-message">Missing event info. Please go back and select tickets again.</p>;
  }

  const { title, price, quantity, userDetails } = eventData;
  const subtotal = price * quantity;
  const tax = subtotal * 0.05;
  const total = subtotal + tax;

  const email = userDetails?.email || '';

  return (
    <div className="checkout-container">
      <div className="checkout-form-section">
        <h2>Payment for {title}</h2>
        <p>Tickets: {quantity}</p>
        <p>Email: {email}</p>
        <p>Total: ₹{total.toFixed(2)}</p>

        {/* You can integrate the Stripe Checkout here later */}
        <button className="pay-button" onClick={() => navigate('/payment-success')}>
          Pay ₹{total.toFixed(2)}
        </button>
      </div>
    </div>
  );
};

export default PaymentPage;
