import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './PaymentPage.css';

const PaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const eventData = location.state;

  if (!eventData || !eventData.title || !eventData.price || !eventData.quantity) {
    return (
      <div style={{ padding: '40px', textAlign: 'center' }}>
        <h2>Missing event info</h2>
        <p>Please go back and select your tickets again.</p>
        <button onClick={() => navigate('/')}>Go to Events</button>
      </div>
    );
  }

  const { title, price, quantity, selectedSeats } = eventData;
  const subtotal = price * quantity;
  const tax = subtotal * 0.05;
  const total = subtotal + tax;

  return (
    <div className="checkout-container">
      <h2>Payment for {title}</h2>
      <p>Selected Seats: {selectedSeats.join(', ')}</p>
      <p>Tickets: {quantity}</p>
      <p>Subtotal: ₹{subtotal}</p>
      <p>Tax (5%): ₹{tax.toFixed(2)}</p>
      <p><strong>Total: ₹{total.toFixed(2)}</strong></p>

      <button className="pay-button" onClick={() => navigate('/payment-success')}>
        Pay ₹{total.toFixed(2)}
      </button>
    </div>
  );
};

export default PaymentPage;
