import React, { useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './PaymentPage.css';

const PaymentPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const eventData = location.state;

  const { title, price, quantity, userDetails } = eventData || {};

  useEffect(() => {
    if (!eventData || !title || !price || !quantity) {
      console.warn("Missing event info. Redirecting...");
      navigate('/', { replace: true });
    }
  }, [eventData, title, price, quantity, navigate]);

  if (!eventData || !title || !price || !quantity) {
    return (
      <div className="checkout-container">
        <p className="error-message">Missing event info. Please go back and select tickets again.</p>
      </div>
    );
  }

  const subtotal = price * quantity;
  const tax = subtotal * 0.05;
  const total = subtotal + tax;

  return (
    <div className="checkout-container">
      <div className="checkout-form-section">
        <h2>Payment for {title}</h2>
        <p><strong>Tickets:</strong> {quantity}</p>
        <p><strong>Total:</strong> ₹{total.toFixed(2)}</p>

        <div className="customer-details">
          <p><strong>Name:</strong> {userDetails?.name}</p>
          <p><strong>Email:</strong> {userDetails?.email}</p>
          <p><strong>Phone:</strong> {userDetails?.phone}</p>
        </div>

        <button
          className="pay-button"
          onClick={() => navigate('/payment-success')}
        >
          Pay ₹{total.toFixed(2)}
        </button>
      </div>
    </div>
  );
};

export default PaymentPage;
