import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './EventsPage.css';

const EventDetailsPage = () => {
  const { eventId } = useParams();
  const [event, setEvent] = useState(null);

  // Mock data for event details
  useEffect(() => {
    const mockEvents = [
      {
        id: "1",
        name: "Summer Music Festival",
        date: "2025-06-15",
        location: "Central Park, New York",
        image: "https://example.com/festival.jpg",
        description: "Join us for an unforgettable night of live music."
      },
      {
        id: "2",
        name: "Winter Wonderland Concert",
        date: "2025-12-20",
        location: "Madison Square Garden, New York",
        image: "https://example.com/concert.jpg",
        description: "Experience the magic of winter with live music."
      }
    ];
    const fetchedEvent = mockEvents.find(e => e.id === eventId);
    setEvent(fetchedEvent);
  }, [eventId]);

  if (!event) return <div>Loading...</div>;

  return (
    <div className="event-details">
      <img src={event.image} alt={event.name} className="details-image" />
      <div className="details-content">
        <h1>{event.name}</h1>
        <p>{new Date(event.date).toLocaleDateString()} â€¢ {event.location}</p>
        <p>{event.description}</p>
      </div>
    </div>
  );
};

export default EventDetailsPage;
