// src/pages/SeatSelectionPage.js
import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SeatSelectionPage.css';

const seatRows = [
  { row: 'A', price: 150 },
  { row: 'B', price: 150 },
  { row: 'C', price: 250 },
  { row: 'D', price: 250 },
  { row: 'E', price: 250 },
  { row: 'F', price: 250 },
  { row: 'G', price: 250 },
  { row: 'H', price: 250 }
];

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state?.event;

  const [selectedSeats, setSelectedSeats] = useState([]);

  if (!event) return <div className="error-message">Event data missing. Please go back.</div>;

  const handleSelect = (row, seatNumber, price) => {
    const seatId = `${row}${seatNumber}`;
    const exists = selectedSeats.find(seat => seat.id === seatId);
    if (exists) {
      setSelectedSeats(prev => prev.filter(seat => seat.id !== seatId));
    } else {
      setSelectedSeats(prev => [...prev, { id: seatId, price }]);
    }
  };

  const handlePayment = () => {
    if (selectedSeats.length === 0) {
      alert("Please select at least one seat.");
      return;
    }

    navigate('/payment', {
      state: {
        title: event.title,
        quantity: selectedSeats.length,
        price: selectedSeats.reduce((sum, s) => sum + s.price, 0),
        selectedSeats
      }
    });
  };

  return (
    <div className="seat-selection-container">
      <h2>Select Seats for {event.title}</h2>
      <div className="seat-layout">
        {seatRows.map(({ row, price }) => (
          <div key={row} className="seat-row">
            <span className="row-label">{row}</span>
            <div className="seats">
              {Array.from({ length: 14 }).map((_, i) => (
                <div
                  key={i}
                  className={`seat ${
                    selectedSeats.find(s => s.id === `${row}${i + 1}`) ? 'selected' : ''
                  }`}
                  onClick={() => handleSelect(row, i + 1, price)}
                >
                  {i + 1}
                </div>
              ))}
            </div>
            <span className="price-label">₹{price}</span>
          </div>
        ))}
      </div>

      <div className="screen">SCREEN THIS WAY</div>

      <div className="summary">
        <p>Selected Seats: {selectedSeats.map(s => s.id).join(', ') || 'None'}</p>
        <p>Total: ₹{selectedSeats.reduce((sum, s) => sum + s.price, 0)}</p>
        <button onClick={handlePayment} className="pay-btn">
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
