import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import BookingModal from '../components/BookingModal';
import './EventDetailsPage.css';

const EventDetailsPage = () => {
  const { eventId } = useParams();
  const navigate = useNavigate();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Transform the backend event details to a format expected by the UI
  const transformEventDetails = (backendDetails) => {
    // Here we assume the backend is for a concert.
    // Adjust as needed if you support movies or theatre.
    return {
      id: backendDetails.eventId,
      title: backendDetails.name,
      date: backendDetails.eventDate,
      time: backendDetails.startTime, // You may format this if needed
      poster: backendDetails.posterImage || "https://via.placeholder.com/800x300",
      // As backend doesn't provide a proper location string, we set a default.
      location: "Mumbai",
      type: "concert", // For this example; you could also pass backend type info if needed.
      price: 0,       // Default price; update if your backend provides pricing.
      details: {
        host: backendDetails.host,
        performers: backendDetails.performers,
        sponsors: backendDetails.sponsors,
        duration: "N/A", // Backend doesn't provide duration; adjust as needed.
      },
    };
  };

  useEffect(() => {
    fetch(`http://localhost:3000/api/events/${eventId}`)
      .then((res) => {
        if (!res.ok) {
          throw new Error("Failed to fetch event details");
        }
        return res.json();
      })
      .then((data) => {
        console.log("Fetched event details:", data);
        if (data.eventDetails) {
          const transformed = transformEventDetails(data.eventDetails);
          setEvent(transformed);
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching event details:", err);
        setError(err.message);
        setLoading(false);
      });
  }, [eventId]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!event) return <div>No event details available</div>;

  return (
    <div className="event-details-page">
      <div
        className="event-hero"
        style={{ backgroundImage: `url(${event.poster})` }}
      >
        <div className="hero-overlay">
          <h1 className="event-title">{event.title}</h1>
        </div>
      </div>

      <div className="event-content">
        <div className="event-meta-grid">
          <div className="meta-item">
            <span className="meta-label">Date & Time</span>
            <span className="meta-value">
              {new Date(event.date).toLocaleDateString('en-IN')} • {event.time}
            </span>
          </div>
          <div className="meta-item">
            <span className="meta-label">Location</span>
            <span className="meta-value">{event.location}</span>
          </div>
          <div className="meta-item">
            <span className="meta-label">Duration</span>
            <span className="meta-value">{event.details.duration}</span>
          </div>
        </div>

        {event.type === 'concert' && (
          <div className="details-section">
            <h2>Concert Details</h2>
            <div className="detail-item">
              <span>Host:</span>
              <span>{event.details.host}</span>
            </div>
            <div className="detail-item">
              <span>Performers:</span>
              <div className="artist-list">
                {event.details.performers && event.details.performers.map((artist, i) => (
                  <div key={i} className="artist-tag">{artist}</div>
                ))}
              </div>
            </div>
            <div className="detail-item">
              <span>Sponsors:</span>
              <div className="sponsor-list">
                {event.details.sponsors && event.details.sponsors.map((sponsor, i) => (
                  <div key={i} className="sponsor-tag">{sponsor}</div>
                ))}
              </div>
            </div>
          </div>
        )}

        {event.type === 'movie' && (
          <div className="details-section">
            <h2>Movie Details</h2>
            {/* Update with movie-specific details if applicable */}
          </div>
        )}

        <div className="booking-cta">
          <div className="price-section">
            <span className="price-label">Starting from</span>
            <span className="price">₹{event.price}</span>
          </div>
          <button
            className="book-button"
            onClick={() => setShowModal(true)}
          >
            Book Tickets
          </button>
        </div>
      </div>

      {showModal && <BookingModal event={event} onClose={() => setShowModal(false)} />}
    </div>
  );
};

export default EventDetailsPage;
