// src/pages/SeatSelectionPage.js
import React, { useState } from 'react';
import './SeatSelectionPage.css';
import { useLocation, useNavigate } from 'react-router-dom';

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state?.event;

  const rows = ['H', 'G', 'F', 'E', 'D', '', 'C', '', 'B', 'A']; // reverse to show A at bottom
  const seatsPerRow = 14;

  const getSeatPrice = (row) => (['A', 'B'].includes(row) ? 150 : 250);

  const [selectedSeats, setSelectedSeats] = useState([]);

  const handleToggleSeat = (seat) => {
    setSelectedSeats((prev) =>
      prev.includes(seat) ? prev.filter((s) => s !== seat) : [...prev, seat]
    );
  };

  const handlePayment = () => {
    const total = selectedSeats.reduce(
      (sum, seat) => sum + getSeatPrice(seat.split('-')[0]),
      0
    );

    navigate('/payment', {
      state: {
        title: event?.title || 'Untitled Event',
        price: total,
        quantity: selectedSeats.length,
        selectedSeats,
        userDetails: { email: '' },
      },
    });
  };

  if (!event) {
    return <div className="missing-event">Event data missing. Please go back.</div>;
  }

  return (
    <div className="seat-page">
      <h2 className="select-heading">Select Seats for {event.title}</h2>

      <div className="screen-container">
        <div className="screen">SCREEN</div>
      </div>

      <div className="seats-grid">
        {rows.map((row, rowIndex) =>
          row === '' ? (
            <div key={`gap-${rowIndex}`} className="row-gap" />
          ) : (
            <div key={row} className="seat-row">
              <div className="row-label">{row}</div>
              {[...Array(seatsPerRow)].map((_, i) => {
                const seatId = `${row}-${i + 1}`;
                const price = getSeatPrice(row);
                const isSelected = selectedSeats.includes(seatId);
                const isSpacer = (i + 1) % 7 === 0;

                return (
                  <div key={seatId} className="seat-wrap">
                    {isSpacer ? <div className="spacer" /> : null}
                    <div
                      className={`seat ${isSelected ? 'selected' : ''} ${
                        price === 150 ? 'low-price' : 'high-price'
                      }`}
                      onClick={() => handleToggleSeat(seatId)}
                    >
                      {i + 1}
                    </div>
                  </div>
                );
              })}
              <div className="row-label">{row}</div>
            </div>
          )
        )}
      </div>

      <div className="summary">
        <p>
          Selected Seats: {selectedSeats.length > 0 ? selectedSeats.join(', ') : 'None'}
        </p>
        <p>
          Total: â‚¹
          {selectedSeats.reduce((sum, seat) => sum + getSeatPrice(seat.split('-')[0]), 0)}
        </p>
        <button
          className="proceed-btn"
          disabled={selectedSeats.length === 0}
          onClick={handlePayment}
        >
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
